set [my-speed-x v] to ([sin v] of (my-lifetime))     // Side-to-side drift
set [ghost v] effect to (pick random (20) to (40))   // Slightly transparent
set size to (pick random (20) to (40)) %
set [rotation-speed v] to (pick random (-1) to (1))  // Slow spin
set [wind-strength v] to (pick random (0.5) to (1.5)) // Wind effect

