        set [my-speed-y v] to ((my-speed-y) * (0.9))
        set [my-speed-x v] to ((my-speed-x) * (0.8))
    end
    
    change [my-lifetime v] by (-1)
    wait (0.05) seconds
end

// CREATE SNOW PILE EFFECT (optional)
