add [Bread] to [shopping-list v]
add [Milk] to [shopping-list v]
add [Eggs] to [shopping-list v]
add [Cheese] to [shopping-list v]

// DISPLAY THE LIST
say (shopping-list) for (2) seconds

// ============ BASIC LIST OPERATIONS ============

// 1. GET LIST LENGTH (how many items)
set [list-size v] to (length of [shopping-list v])
say (join [I have ] (join (list-size) [ items on my list])) for (2) seconds

// 2. ACCESS SPECIFIC ITEM
set [first-item v] to (item (1) of [shopping-list v])
set [last-item v] to (item (length of [shopping-list v]) of [shopping-list v])
say (join [First: ] (first-item)) for (2) seconds
say (join [Last: ] (last-item)) for (2) seconds

// 3. CHECK IF LIST CONTAINS SOMETHING
if <[shopping-list v] contains [Milk] ?> then
    say [I need to buy milk!] for (2) seconds
end

// 4. DELETE ITEMS
delete (1) of [shopping-list v]  // Remove first item (Apples)
delete (last v) of [shopping-list v]  // Remove last item
delete (all v) of [shopping-list v]  // Clear entire list

// 5. INSERT AT SPECIFIC POSITION
