    <pre style="background: #263238; color: white; padding: 15px; border-radius: 5px; overflow-x: auto; margin-top: 10px;">
// ============ LOOPING THROUGH LISTS ============

// METHOD 1: REPEAT FOR EACH ITEM
set [i v] to [1]
repeat (length of [shopping-list v])
    set [current-item v] to (item (i) of [shopping-list v])
    say (join (join [Item ] (i)) (join [: ] (current-item))) for (1) seconds
    change [i v] by (1)
end

// METHOD 2: WHILE LIST NOT EMPTY
repeat until <(length of [shopping-list v]) = [0]>
    set [next-item v] to (item (1) of [shopping-list v])
    say (join [Buying: ] (next-item)) for (1) seconds
    delete (1) of [shopping-list v]  // Remove as we "buy" it
end
say [All done shopping!] for (2) seconds

// METHOD 3: PROCESSING WITH INDEX
set [total-cost v] to [0]
set [i v] to [1]
repeat (length of [shopping-list v])
    set [item-name v] to (item (i) of [shopping-list v])
    
    // Calculate price based on item
    if <(item-name) = [Apples]> then
        change [total-cost v] by [5]
    end
    if <(item-name) = [Milk]> then
        change [total-cost v] by [3]
    end
    if <(item-name) = [Bread]> then
        change [total-cost v] by [2]
    end
    
    change [i v] by (1)
end
say (join [Total cost: $] (total-cost)) for (2) seconds

// METHOD 4: FINDING SPECIFIC ITEM
set [found-index v] to [0]
set [search-for v] to [Milk]
set [i v] to [1]
repeat (length of [shopping-list v])
    if <(item (i) of [shopping-list v]) = (search-for)> then
        set [found-index v] to (i)
        stop [this script v]  // Stop searching
    end
    change [i v] by (1)
end

if <(found-index) > [0]> then
    say (join [Found ] (join (search-for) (join [ at position ] (found-index)))) for (2) seconds
else
    say (join (search-for) [ not found]) for (2) seconds
end
