add [0] to [equipment-magic v]
add [2] to [equipment-speed v]

// ============ EQUIP ITEM ============
define equip item (item-name) from slot (inventory-slot)
// CHECK IF ITEM IS EQUIPMENT
set [is-equipment v] to [false]
set [equipment-index v] to [0]
set [i v] to [1]
repeat (length of [equipment-names v])
    if <(item (i) of [equipment-names v]) = (item-name)> then
        set [is-equipment v] to [true]
        set [equipment-index v] to (i)
        stop [this script v]
    end
    change [i v] by (1)
end

