set [i v] to [1]
repeat (length of [equipment-slots v])
    if <(item (i) of [equipment-slots v]) = (slot-type)> then
        set [equip-slot v] to (i)
        stop [this script v]
    end
    change [i v] by (1)
end

// SPECIAL CASE: RINGS (can go in Ring1 or Ring2)
if <(slot-type) = [Ring1]> then
    // Try Ring1 first
    if <(item (7) of [equipment-items v]) = [empty]> then  // Ring1 is position 7
        set [equip-slot v] to [7]
    else
        // Try Ring2
        if <(item (8) of [equipment-items v]) = [empty]> then  // Ring2 is position 8
            set [equip-slot v] to [8]
        else
