    replace item (equip-slot) of [equipment-items v] with (item-name)
    
    // REMOVE FROM INVENTORY
    replace item (inventory-slot) of [inventory-items v] with [empty]
    replace item (inventory-slot) of [inventory-quantities v] with [0]
    
    // UPDATE STATS
    update stats :: custom
    
    say (join (join [Equipped ] (item-name)) [ to ] (join (slot-type) [ slot])) for (2) seconds
    display equipment :: custom
else
    // SLOT OCCUPIED - ASK TO SWAP
    set [current-item v] to (item (equip-slot) of [equipment-items v])
    
    // SWAP ITEMS
    replace item (equip-slot) of [equipment-items v] with (item-name)
    replace item (inventory-slot) of [inventory-items v] with (current-item)
    replace item (inventory-slot) of [inventory-quantities v] with [1]
    
    say (join (join [Swapped ] (current-item)) (join [ for ] (item-name))) for (2) seconds
    update stats :: custom
    display equipment :: custom
end

// ============ UPDATE PLAYER STATS ============
define update stats
set [total-attack v] to [0]
set [total-defense v] to [0]
set [total-magic v] to [0]
set [total-speed v] to [0]

// BASE STATS
set [base-attack v] to [5]
set [base-defense v] to [5]
set [base-magic v] to [5]
set [base-speed v] to [5]

// ADD EQUIPMENT STATS
set [i v] to [1]
repeat (length of [equipment-items v])
    if <not <(item (i) of [equipment-items v]) = [empty]>> then
        set [equip-name v] to (item (i) of [equipment-items v])
        
        // FIND IN EQUIPMENT DATABASE
        set [j v] to [1]
        repeat (length of [equipment-names v])
            if <(item (j) of [equipment-names v]) = (equip-name)> then
                change [total-attack v] by (item (j) of [equipment-attack v])
                change [total-defense v] by (item (j) of [equipment-defense v])
                change [total-magic v] by (item (j) of [equipment-magic v])
                change [total-speed v] by (item (j) of [equipment-speed v])
                stop [this script v]
            end
            change [j v] by (1)
        end
    end
    change [i v] by (1)
end

// CALCULATE TOTALS
set [player-attack v] to ((base-attack) + (total-attack))
set [player-defense v] to ((base-defense) + (total-defense))
set [player-magic v] to ((base-magic) + (total-magic))
set [player-speed v] to ((base-speed) + (total-speed))

// DISPLAY STATS
say (join (join [Attack: ] (player-attack)) (join [ | Defense: ] (player-defense))) for (2) seconds
say (join (join [Magic: ] (player-magic)) (join [ | Speed: ] (player-speed))) for (2) seconds
    </pre>
