        <pre style="background: #f5f5f5; padding: 10px; border-radius: 5px;">
// ============ INVENTORY WITH WEIGHT SYSTEM ============
when green flag clicked

// SETUP INVENTORY
set [inventory-slots v] to [16]  // 4x4 grid
set [max-weight v] to [100]  // kilograms
set [current-weight v] to [0]

// Initialize inventory lists
delete (all v) of [inv-items v]
delete (all v) of [inv-quantities v]
delete (all v) of [inv-weights v]  // Individual item weights

repeat (inventory-slots)
    add [empty] to [inv-items v]
    add [0] to [inv-quantities v]
    add [0] to [inv-weights v]
end

// ============ CALCULATE INVENTORY WEIGHT ============
define calculate weight
set [current-weight v] to [0]
set [i v] to [1]
repeat (inventory-slots)
    if <not <(item (i) of [inv-items v]) = [empty]>> then
        // Item weight Ã— quantity
        set [item-weight v] to (item (i) of [inv-weights v])
        set [item-qty v] to (item (i) of [inv-quantities v])
        change [current-weight v] by ((item-weight) * (item-qty))
    end
    change [i v] by (1)
end

// ============ ADD ITEM WITH WEIGHT CHECK ============
define add item with weight (item-name) (quantity) (weight-per-item)
// Calculate weight of new items
set [new-weight v] to ((weight-per-item) * (quantity))

// Check if we have enough weight capacity
calculate weight :: custom
if <((current-weight) + (new-weight)) > (max-weight)> then
    // Too heavy! Calculate how many we can add
    set [weight-left v] to ((max-weight) - (current-weight))
    set [can-add v] to ([floor v] of ((weight-left) / (weight-per-item)))
    
    if <(can-add) > [0]> then
        add to inventory (item-name) (can-add) :: custom
        say (join (join [Too heavy! Only added ] (can-add)) (join [ of ] (join (quantity) (join [ ] (item-name))))) for (2) seconds
    else
        say [Inventory too heavy! Drop some items first.] for (2) seconds
    end
else
    // Have enough weight capacity
    add to inventory (item-name) (quantity) :: custom
    // Update weight for this item in inventory
    set [slot v] to [1]
    repeat (inventory-slots)
        if <(item (slot) of [inv-items v]) = (item-name)> then
            replace item (slot) of [inv-weights v] with (weight-per-item)
            stop [this script v]
        end
        change [slot v] by (1)
    end
end

// Update weight display
calculate weight :: custom
say (join (join [Weight: ] (current-weight)) (join [/] (join (max-weight) [kg]))) for (2) seconds
