    if <(selected-slot) < [1]> then
        set [selected-slot v] to ((selected-slot) + (inventory-slots))
    end
    go to slot (selected-slot) :: custom
    wait until <not <key [up arrow v] pressed?>>
end

if <key [down arrow v] pressed?> then
    change [selected-slot v] by (4)  // Move down one row
    if <(selected-slot) > (inventory-slots)> then
        set [selected-slot v] to ((selected-slot) - (inventory-slots))
    end
    go to slot (selected-slot) :: custom
    wait until <not <key [down arrow v] pressed?>>
end

// ACTION BUTTONS
if <key [e v] pressed?> then  // Equip/Use
    set [selected-item v] to (item (selected-slot) of [inv-items v])
    if <not <(selected-item) = [empty]>> then
        broadcast (join [use-] (selected-item))
    end
    wait until <not <key [e v] pressed?>>
end

if <key [d v] pressed?> then  // Drop
    set [selected-item v] to (item (selected-slot) of [inv-items v])
    if <not <(selected-item) = [empty]>> then
