                ğŸ“­ No notes yet. Add your first note above!
            &lt;/div&gt;
        &lt;/div&gt;
        
        &lt;div class=\"demo-buttons\"&gt;
            &lt;button class=\"demo-btn\" onclick=\"addDemoNote()\"&gt;Add Demo Note&lt;/button&gt;
            &lt;button class=\"demo-btn\" onclick=\"addMultipleNotes(5)\"&gt;Add 5 Random Notes&lt;/button&gt;
            &lt;button class=\"demo-btn\" onclick=\"clearAllNotes()\"&gt;Clear All Notes&lt;/button&gt;
        &lt;/div&gt;
    &lt;/div&gt;

    &lt;script&gt;
        // ========== VARIABLES ==========
        let currentColor = '#fed7d7'; // Default color
        let noteCounter = 1;
        
        // ========== FUNCTIONS ==========
        function setNoteColor(color) {
            currentColor = color;
            document.getElementById(\"note-input\").style.borderColor = color;
        }
        
        function addNote() {
            let input = document.getElementById(\"note-input\");
            let noteText = input.value.trim();
            
            if (noteText === \"\") {
                alert(\"Please enter some text for your note!\");
                return;
            }
            
            // Hide empty state if this is the first note
            let emptyState = document.getElementById(\"empty-state\");
            if (emptyState.style.display !== \"none\") {
                emptyState.style.display = \"none\";
            }
            
            // 1. CREATE THE NOTE CARD CONTAINER
            let noteCard = document.createElement(\"div\");
            noteCard.className = \"note-card\";
            
            // 2. CREATE AND ADD THE COLOR INDICATOR
            let colorIndicator = document.createElement(\"div\");
            colorIndicator.className = \"note-color\";
            colorIndicator.style.backgroundColor = currentColor;
            noteCard.appendChild(colorIndicator);
            
            // 3. CREATE AND ADD THE DATE DISPLAY
            let dateElement = document.createElement(\"div\");
            dateElement.className = \"note-date\";
            let now = new Date();
            dateElement.textContent = now.toLocaleString();
            noteCard.appendChild(dateElement);
            
            // 4. CREATE AND ADD THE NOTE TEXT
            let textElement = document.createElement(\"div\");
            textElement.className = \"note-text\";
            textElement.textContent = noteText;
            noteCard.appendChild(textElement);
            
            // 5. CREATE AND ADD THE DELETE BUTTON
            let deleteButton = document.createElement(\"button\");
            deleteButton.className = \"delete-btn\";
            deleteButton.textContent = \"X\";
            deleteButton.onclick = function() {
                noteCard.remove();
                
                // Check if we need to show empty state again
                let container = document.getElementById(\"notes-container\");
                if (container.children.length === 1) { // Only empty state remains
                    emptyState.style.display = \"block\";
                }
            };
            noteCard.appendChild(deleteButton);
            
            // 6. ADD CLICK EVENT TO CHANGE COLOR
            colorIndicator.onclick = function() {
                // Cycle through colors
                const colors = ['#fed7d7', '#feebc8', '#c6f6d5', '#bee3f8', '#e9d8fd'];
                let currentIndex = colors.indexOf(currentColor);
                let nextIndex = (currentIndex + 1) % colors.length;
                currentColor = colors[nextIndex];
                colorIndicator.style.backgroundColor = currentColor;
            };
            
            // 7. APPEND TO THE CONTAINER
            let container = document.getElementById(\"notes-container\");
            container.appendChild(noteCard);
            
            // 8. CLEAR INPUT AND RESET FOCUS
            input.value = \"\";
            input.focus();
            
            // 9. ADD ANIMATION
            noteCard.style.opacity = \"0\";
            noteCard.style.transform = \"translateY(20px)\";
            
            setTimeout(() => {
                noteCard.style.transition = \"opacity 0.3s, transform 0.3s\";
                noteCard.style.opacity = \"1\";
                noteCard.style.transform = \"translateY(0)\";
            }, 10);
            
            noteCounter++;
        }
        
        // ========== DEMO FUNCTIONS ==========
        function addDemoNote() {
            const demoNotes = [
                \"Don't forget to buy milk!\",
                \"Call Grandma on Sunday\",
                \"JavaScript is awesome! ğŸ¤©\",
                \"Complete homework by Friday\",
                \"Practice coding for 1 hour daily\",
                \"Learn something new today!\",
                \"Remember: You're doing great! ğŸ’ª\"
            ];
            
            let randomNote = demoNotes[Math.floor(Math.random() * demoNotes.length)];
            document.getElementById(\"note-input\").value = randomNote;
            addNote();
        }
        
        function addMultipleNotes(count) {
            for (let i = 0; i < count; i++) {
                setTimeout(() => {
                    addDemoNote();
                }, i * 100); // Stagger the creation
            }
        }
        
        function clearAllNotes() {
            let container = document.getElementById(\"notes-container\");
            let emptyState = document.getElementById(\"empty-state\");
            
            // Remove all note cards (skip the empty state)
            while (container.children.length > 1) {
                container.removeChild(container.lastChild);
            }
            
            // Show empty state
            emptyState.style.display = \"block\";
        }
        
        // ========== ENTER KEY SUPPORT ==========
        document.getElementById(\"note-input\").addEventListener(\"keypress\", function(event) {
            if (event.key === \"Enter\") {
                addNote();
            }
        });
        
        // ========== ADD INITIAL DEMO NOTES ==========
        // Add 2 demo notes on load
        setTimeout(() => {
            addDemoNote();
            setTimeout(addDemoNote, 200);
        }, 500);
    &lt;/script&gt;
