    &lt;script&gt;
        // ========== STATE MANAGEMENT ==========
        let tasks = [];
        let currentPriority = 'low';
        
        // ========== DOM ELEMENTS ==========
        const taskInput = document.getElementById('task-input');
        const addButton = document.getElementById('add-button');
        const tasksContainer = document.getElementById('tasks-container');
        const emptyState = document.getElementById('empty-state');
        const priorityButtons = document.querySelectorAll('.priority-btn');
        const filterSelect = document.getElementById('filter-select');
        
        // Stats elements
        const totalTasksElement = document.getElementById('total-tasks');
        const completedTasksElement = document.getElementById('completed-tasks');
        const pendingTasksElement = document.getElementById('pending-tasks');
        
        // ========== HELPER FUNCTIONS ==========
        function updateStats() {
            const total = tasks.length;
            const completed = tasks.filter(task => task.completed).length;
            const pending = total - completed;
            
            totalTasksElement.textContent = total;
            completedTasksElement.textContent = completed;
            pendingTasksElement.textContent = pending;
        }
        
        function saveToLocalStorage() {
            localStorage.setItem('todoTasks', JSON.stringify(tasks));
            console.log('Tasks saved to localStorage!');
        }
        
        function loadFromLocalStorage() {
            const savedTasks = localStorage.getItem('todoTasks');
            if (savedTasks) {
                tasks = JSON.parse(savedTasks);
                renderTasks();
                updateStats();
                console.log('Tasks loaded from localStorage!');
            }
        }
        
        function hideEmptyState() {
            emptyState.style.display = 'none';
        }
        
        function showEmptyState() {
            emptyState.style.display = 'block';
        }
        
        // ========== TASK CREATION ==========
        function createTaskElement(task) {
            // Create the main task item container
            const taskItem = document.createElement('li');
            taskItem.className = 'task-item';
            taskItem.dataset.id = task.id;
            
            if (task.completed) {
                taskItem.classList.add('completed');
            }
            
            // Create checkbox
            const checkbox = document.createElement('div');
            checkbox.className = 'task-checkbox';
            if (task.completed) checkbox.classList.add('checked');
            checkbox.innerHTML = task.completed ? 'âœ“' : '';
            
            checkbox.addEventListener('click', () => toggleTaskComplete(task.id));
            
            // Create task content container
            const contentDiv = document.createElement('div');
            contentDiv.className = 'task-content';
            
            // Create task text
            const taskText = document.createElement('div');
            taskText.className = 'task-text';
            taskText.textContent = task.text;
            
            // Create task metadata
            const metaDiv = document.createElement('div');
            metaDiv.className = 'task-meta';
            
            const prioritySpan = document.createElement('span');
            prioritySpan.className = \`
                task - priority priority - $ {
                    task.priority
                }
                \`;
            prioritySpan.textContent = \`
                $ {
                    task.priority.charAt(0).toUpperCase() + task.priority.slice(1)
                }
                Priority\`;
            
            const dateSpan = document.createElement('span');
            dateSpan.textContent = new Date(task.createdAt).toLocaleDateString();
            
            metaDiv.appendChild(prioritySpan);
            metaDiv.appendChild(dateSpan);
            
            contentDiv.appendChild(taskText);
            contentDiv.appendChild(metaDiv);
            
            // Create action buttons
            const actionsDiv = document.createElement('div');
            actionsDiv.className = 'task-actions';
            
            const editButton = document.createElement('button');
            editButton.className = 'action-btn edit-btn';
            editButton.innerHTML = '<i class="fas fa-edit"></i>';
            editButton.title = 'Edit task';
            editButton.addEventListener('click', () => editTask(task.id));
            
            const deleteButton = document.createElement('button');
            deleteButton.className = 'action-btn delete-btn';
            deleteButton.innerHTML = '<i class="fas fa-trash"></i>';
            deleteButton.title = 'Delete task';
            deleteButton.addEventListener('click', () => deleteTask(task.id));
            
            actionsDiv.appendChild(editButton);
            actionsDiv.appendChild(deleteButton);
            
            // Assemble everything
            taskItem.appendChild(checkbox);
            taskItem.appendChild(contentDiv);
            taskItem.appendChild(actionsDiv);
            
            return taskItem;
        }
        
        function renderTasks() {
            // Clear current tasks
            tasksContainer.innerHTML = '';
            
            // Filter tasks based on selected filter
            const filter = filterSelect.value;
            let filteredTasks = tasks;
            
            if (filter === 'pending') {
                filteredTasks = tasks.filter(task => !task.completed);
            } else if (filter === 'completed') {
                filteredTasks = tasks.filter(task => task.completed);
            }
            
            // Render filtered tasks
            if (filteredTasks.length === 0) {
                tasksContainer.appendChild(emptyState);
                showEmptyState();
            } else {
                hideEmptyState();
                filteredTasks.forEach(task => {
                    const taskElement = createTaskElement(task);
                    tasksContainer.appendChild(taskElement);
                });
            }
            
            updateStats();
        }
        
        // ========== TASK OPERATIONS ==========
        function addTask() {
            const text = taskInput.value.trim();
            
            if (text === '') {
                alert('Please enter a task!');
                return;
            }
            
            const newTask = {
                id: Date.now(), // Simple unique ID
                text: text,
                priority: currentPriority,
                completed: false,
                createdAt: new Date().toISOString()
            };
            
            tasks.push(newTask);
            taskInput.value = '';
            taskInput.focus();
            
            renderTasks();
            saveToLocalStorage();
        }
        
        function toggleTaskComplete(taskId) {
            const taskIndex = tasks.findIndex(task => task.id === taskId);
            if (taskIndex !== -1) {
                tasks[taskIndex].completed = !tasks[taskIndex].completed;
                renderTasks();
                saveToLocalStorage();
            }
        }
        
        function editTask(taskId) {
            const taskIndex = tasks.findIndex(task => task.id === taskId);
            if (taskIndex === -1) return;
            
            const newText = prompt('Edit your task:', tasks[taskIndex].text);
            if (newText !== null && newText.trim() !== '') {
                tasks[taskIndex].text = newText.trim();
                renderTasks();
                saveToLocalStorage();
            }
        }
        
        function deleteTask(taskId) {
            if (confirm('Are you sure you want to delete this task?')) {
                tasks = tasks.filter(task => task.id !== taskId);
                renderTasks();
                saveToLocalStorage();
            }
        }
        
        function clearCompletedTasks() {
            tasks = tasks.filter(task => !task.completed);
            renderTasks();
            saveToLocalStorage();
        }
        
        function clearAllTasks() {
            if (tasks.length === 0) {
                alert('No tasks to clear!');
                return;
            }
            
            if (confirm('Are you sure you want to delete ALL tasks?')) {
                tasks = [];
                renderTasks();
                saveToLocalStorage();
            }
        }
        
        // ========== EVENT LISTENERS ==========
        addButton.addEventListener('click', addTask);
        
        taskInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') {
                addTask();
            }
        });
        
        // Priority button selection
        priorityButtons.forEach(button => {
            button.addEventListener('click', () => {
                priorityButtons.forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                currentPriority = button.dataset.priority;
            });
        });
        
        // Filter change
        filterSelect.addEventListener('change', renderTasks);
        
        // Control buttons
        document.getElementById('clear-completed').addEventListener('click', clearCompletedTasks);
        document.getElementById('clear-all').addEventListener('click', clearAllTasks);
        document.getElementById('save-tasks').addEventListener('click', saveToLocalStorage);
        document.getElementById('load-tasks').addEventListener('click', loadFromLocalStorage);
        
        // ========== INITIALIZATION ==========
        // Load tasks from localStorage on page load
        loadFromLocalStorage();
        
        // Add some sample tasks if empty
        if (tasks.length === 0) {
            tasks = [
                {
                    id: 1,
                    text: 'Learn DOM Manipulation',
                    priority: 'high',
                    completed: true,
                    createdAt: new Date().toISOString()
                },
                {
                    id: 2,
                    text: 'Build a To-Do List App',
                    priority: 'high',
                    completed: false,
                    createdAt: new Date().toISOString()
                },
                {
                    id: 3,
                    text: 'Add localStorage functionality',
                    priority: 'medium',
                    completed: false,
                    createdAt: new Date().toISOString()
                }
            ];
            renderTasks();
            saveToLocalStorage();
        }
        
        // Initial render
        renderTasks();
    &lt;/script&gt;
