    &lt;script&gt;
        // ========== TREASURE HUNT GAME ==========
        let boxesOpened = 0;
        let treasureFound = 0;
        let attemptsLeft = 10;
        let treasureLocation = null;
        
        // Initialize the game
        function initGame() {
            const grid = document.getElementById(\"treasure-grid\");
            grid.innerHTML = \"\";
            
            // Reset stats
            boxesOpened = 0;
            treasureFound = 0;
            attemptsLeft = 10;
            
            // Update displays
            updateStats();
            
            // Hide the treasure in a random box
            treasureLocation = Math.floor(Math.random() * 25);
            
            // Create 5x5 grid of boxes
            for (let i = 0; i < 25; i++) {
                const box = document.createElement(\"div\");
                box.className = \"treasure-box\";
                box.dataset.index = i;
                
                // Add click event
                box.addEventListener(\"click\", function(event) {
                    openBox(this, event);
                });
                
                // Add hover events
                box.addEventListener(\"mouseenter\", function(event) {
                    this.style.transform = \"scale(1.05)\";
                    this.style.boxShadow = \"0 0 20px rgba(255,255,255,0.3)\";
                });
                
                box.addEventListener(\"mouseleave\", function(event) {
                    if (!this.classList.contains(\"opened\")) {
                        this.style.transform = \"scale(1)\";
