            document.getElementById(\"message\").textContent = 
                \"Click on boxes to find the hidden treasure! You have 10 attempts.\";
        }
        
        function openBox(boxElement, event) {
            // Check if game is over
            if (attemptsLeft <= 0 || boxElement.classList.contains(\"opened\")) {
                return;
            }
            
            const index = parseInt(boxElement.dataset.index);
            
            // Mark as opened
            boxElement.classList.add(\"opened\");
            boxesOpened++;
            attemptsLeft--;
            
            // Check if it's the treasure
            if (index === treasureLocation) {
                boxElement.classList.add(\"treasure\");
                treasureFound++;
                document.getElementById(\"message\").innerHTML = 
                    \"ğŸ‰ <b>YOU FOUND THE TREASURE!</b> ğŸ‰<br>It was in box number \" + (index + 1);
                
                // Add celebration effect
                boxElement.style.animation = \"pulse 0.5s infinite\";
                setTimeout(() => {
                    boxElement.style.animation = \"\";
                }, 2000);
            } else {
                document.getElementById(\"message\").textContent = 
                    \"Box \" + (index + 1) + \" is empty. Keep looking! \" + attemptsLeft + \" attempts left.\";
            }
            
            // Check game end conditions
            if (treasureFound > 0) {
                document.getElementById(\"message\").innerHTML += 
                    \"<br><br>ğŸŠ <b>YOU WIN!</b> Refresh or click Reset to play again!\";
                disableAllBoxes();
            } else if (attemptsLeft <= 0) {
                document.getElementById(\"message\").innerHTML = 
                    \"ğŸ’€ <b>GAME OVER!</b> You ran out of attempts.<br>The treasure was in box \" + (treasureLocation + 1);
                revealTreasure();
            }
            
            updateStats();
            
            // Log event details
            console.log(\"Click event:\", event);
            console.log(\"Clicked at:\", event.clientX, event.clientY);
            console.log(\"Target:\", event.target);
        }
        
        function disableAllBoxes() {
            const boxes = document.querySelectorAll(\".treasure-box\");
            boxes.forEach(box => {
                box.style.cursor = \"not-allowed\";
                box.onclick = null;
            });
        }
        
        function revealTreasure() {
            const boxes = document.querySelectorAll(\".treasure-box\");
            boxes.forEach((box, index) => {
                if (index === treasureLocation) {
                    box.classList.add(\"treasure\");
                    box.style.animation = \"pulse 0.5s infinite\";
                }
            });
        }
        
        function updateStats() {
            document.getElementById(\"boxes-opened\").textContent = boxesOpened;
            document.getElementById(\"treasure-found\").textContent = treasureFound;
            document.getElementById(\"attempts-left\").textContent = attemptsLeft;
        }
        
        // ========== EVENT PRACTICE ZONE ==========
        // Hover Box
        const hoverBox = document.getElementById(\"hover-box\");
        const hoverCoords = document.getElementById(\"hover-coords\");
        
        hoverBox.addEventListener(\"mousemove\", function(event) {
            const x = event.clientX - this.getBoundingClientRect().left;
            const y = event.clientY - this.getBoundingClientRect().top;
            
            hoverCoords.textContent = \`Coordinates: (\${Math.floor(x)}, \${Math.floor(y)})\`;
            
            // Change color based on position
            const red = Math.floor((x / 100) * 255);
            const blue = Math.floor((y / 100) * 255);
            this.style.backgroundColor = \`rgb(\${red}, 100, \${blue})\`;
        });
        
        hoverBox.addEventListener(\"mouseenter\", function() {
            this.style.border = \"3px solid gold\";
        });
        
        hoverBox.addEventListener(\"mouseleave\", function() {
            this.style.border = \"none\";
            this.style.backgroundColor = \"#4A90E2\";
            hoverCoords.textContent = \"Coordinates: (0, 0)\";
        });
        
        // Click Box
        const clickBox = document.getElementById(\"click-box\");
        const clickCount = document.getElementById(\"click-count\");
        const clickType = document.getElementById(\"click-type\");
