        const doubleClickMessage = document.getElementById(\"double-click-message\");
        let lastClickTime = 0;
        
        doubleClickBox.addEventListener(\"click\", function(event) {
            const currentTime = new Date().getTime();
            const timeSinceLastClick = currentTime - lastClickTime;
            
            if (timeSinceLastClick < 300) { // 300ms = double click threshold
                doubleClickMessage.innerHTML = \"ğŸ¯ <b>DOUBLE CLICK DETECTED!</b>\";
                this.style.backgroundColor = \"#d69e2e\";
                
                setTimeout(() => {
                    this.style.backgroundColor = \"#4A90E2\";
                    doubleClickMessage.textContent = \"Double-click the box!\";
                }, 1000);
            }
            
            lastClickTime = currentTime;
        });
        
        // ========== GAME CONTROLS ==========
        document.getElementById(\"reset-btn\").addEventListener(\"click\", initGame);
        
        document.getElementById(\"hint-btn\").addEventListener(\"click\", function() {
            if (attemptsLeft > 0 && treasureFound === 0) {
                const row = Math.floor(treasureLocation / 5) + 1;
