        // ========== KEY DISPLAY SETUP ==========
        const keyDisplay = document.getElementById('key-display');
        const keyConfig = [
            { code: 'KeyW', label: 'W / â†‘', display: 'W' },
            { code: 'KeyA', label: 'A / â†', display: 'A' },
            { code: 'KeyS', label: 'S / â†“', display: 'S' },
            { code: 'KeyD', label: 'D / â†’', display: 'D' },
            { code: 'Space', label: 'Space', display: 'â£' },
            { code: 'ShiftLeft', label: 'Shift', display: 'â‡§' },
            { code: 'KeyE', label: 'E', display: 'E' },
            { code: 'KeyR', label: 'R', display: 'R' },
            { code: 'Escape', label: 'Esc', display: 'Esc' }
        ];
        
        // Create key display elements
        keyConfig.forEach(key => {
            const keyElement = document.createElement('div');
            keyElement.className = 'key';
            keyElement.id = \`key-\${key.code}\`;
            keyElement.innerHTML = \`
                <div style="font-size: 1.5em;">\${key.display}</div>
                <div class="key-label">\${key.label}</div>
            \`;
            keyDisplay.appendChild(keyElement);
        });
        
        // ========== KEYBOARD EVENT HANDLERS ==========
        document.addEventListener('keydown', function(event) {
            // Prevent default for game keys
            if (['Space', 'ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(event.code)) {
                event.preventDefault();
            }
            
            // Track key state
            keys[event.code] = true;
            keysPressedCount++;
            
            // Update key display
            updateKeyDisplay(event.code, true);
            
            // Log the event
            if (isLogging) {
                logKeyEvent('keydown', event);
            }
            
            // Special key actions
            switch(event.code) {
                case 'KeyR':
                    resetGame();
                    break;
                case 'Escape':
                    togglePause();
                    break;
            }
            
            updateStats();
        });
        
        document.addEventListener('keyup', function(event) {
            // Track key state
            keys[event.code] = false;
            
            // Update key display
            updateKeyDisplay(event.code, false);
            
            // Log the event
            if (isLogging) {
                logKeyEvent('keyup', event);
            }
            
            updateStats();
        });
        
        function updateKeyDisplay(keyCode, isPressed) {
            const keyElement = document.getElementById(\`key-\${keyCode}\`);
            if (keyElement) {
                if (isPressed) {
                    keyElement.classList.add('active');
                } else {
                    keyElement.classList.remove('active');
                }
            }
        }
        
        function logKeyEvent(type, event) {
            const log = document.getElementById('key-log');
            const entry = document.createElement('div');
            entry.className = \`key-log-entry \${type}\`;
            
            const time = new Date().toLocaleTimeString();
            const modifiers = [];
            if (event.ctrlKey) modifiers.push('Ctrl');
            if (event.shiftKey) modifiers.push('Shift');
            if (event.altKey) modifiers.push('Alt');
            if (event.metaKey) modifiers.push('Cmd');
            
            const modifierText = modifiers.length > 0 ? \` + \${modifiers.join('+')}\` : '';
            
            entry.textContent = \`[\${time}] \${type}: \${event.key} (\${event.code})\${modifierText}\`;
            
            log.appendChild(entry);
            log.scrollTop = log.scrollHeight;
            
            // Keep only last 20 entries
            while (log.children.length > 20) {
                log.removeChild(log.firstChild);
            }
        }
        
        // ========== GAME LOGIC ==========
        function initGame() {
            // Reset game state
            score = 0;
            itemsCollected = 0;
            keysPressedCount = 0;
            player.x = 400;
            player.y = 200;
            
            // Create collectibles
            collectibles = [];
            for (let i = 0; i < 10; i++) {
                collectibles.push({
                    x: Math.random() * (canvas.width - 20),
                    y: Math.random() * (canvas.height - 20),
                    collected: false
                });
            }
            
            // Create obstacles
            obstacles = [
                { x: 100, y: 150, width: 200, height: 20 },
                { x: 500, y: 250, width: 150, height: 20 },
                { x: 300, y: 350, width: 250, height: 20 }
            ];
            
            updateStats();
        }
        
        function updateGame() {
            // Clear canvas
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            
            // Update player movement
            let moveSpeed = player.speed;
            if (keys['ShiftLeft'] || keys['ShiftRight']) {
                moveSpeed = player.speed * 2;
            }
            
            // Movement controls
            if (keys['KeyW'] || keys['ArrowUp']) {
                player.y -= moveSpeed;
            }
            if (keys['KeyS'] || keys['ArrowDown']) {
                player.y += moveSpeed;
            }
            if (keys['KeyA'] || keys['ArrowLeft']) {
                player.x -= moveSpeed;
            }
            if (keys['KeyD'] || keys['ArrowRight']) {
                player.x += moveSpeed;
            }
            
            // Jump with space
            if ((keys['Space'] || keys['Spacebar']) && !player.isJumping) {
                player.isJumping = true;
                player.jumpProgress = 0;
            }
            
            // Handle jumping
            if (player.isJumping) {
                player.jumpProgress += 0.1;
                const jumpOffset = Math.sin(player.jumpProgress * Math.PI) * player.jumpHeight;
                player.y = 200 - jumpOffset;
                
                if (player.jumpProgress >= 1) {
                    player.isJumping = false;
                    player.y = 200;
                }
            }
            
            // Keep player in bounds
            player.x = Math.max(0, Math.min(canvas.width - player.width, player.x));
            player.y = Math.max(0, Math.min(canvas.height - player.height, player.y));
            
            // Draw obstacles
            ctx.fillStyle = '#38a169';
            obstacles.forEach(obstacle => {
                ctx.fillRect(obstacle.x, obstacle.y, obstacle.width, obstacle.height);
                
                // Collision detection
                if (player.x < obstacle.x + obstacle.width &&
                    player.x + player.width > obstacle.x &&
                    player.y < obstacle.y + obstacle.height &&
                    player.y + player.height > obstacle.y) {
                    // Push player out of obstacle
                    if (keys['KeyD'] || keys['ArrowRight']) {
                        player.x = obstacle.x - player.width;
                    }
                    if (keys['KeyA'] || keys['ArrowLeft']) {
                        player.x = obstacle.x + obstacle.width;
                    }
                    if (keys['KeyW'] || keys['ArrowUp']) {
                        player.y = obstacle.y + obstacle.height;
                    }
                    if (keys['KeyS'] || keys['ArrowDown']) {
                        player.y = obstacle.y - player.height;
                    }
                }
            });
            
            // Draw collectibles
            collectibles.forEach((item, index) => {
                if (!item.collected) {
                    ctx.fillStyle = '#d69e2e';
                    ctx.beginPath();
                    ctx.arc(item.x + 10, item.y + 10, 10, 0, Math.PI * 2);
                    ctx.fill();
                    
                    // Check collection with E key
                    if (keys['KeyE']) {
                        const distance = Math.sqrt(
                            Math.pow(player.x + player.width/2 - (item.x + 10), 2) +
                            Math.pow(player.y + player.height/2 - (item.y + 10), 2)
                        );
                        
                        if (distance < 30) { // Within collection range
                            item.collected = true;
                            itemsCollected++;
                            score += 100;
                            updateStats();
                        }
                    }
                }
            });
            
            // Draw player
            ctx.fillStyle = '#e53e3e';
            ctx.beginPath();
            ctx.arc(player.x + player.width/2, player.y + player.height/2, player.width/2, 0, Math.PI * 2);
            ctx.fill();
            
            // Draw player eyes (facing direction)
            ctx.fillStyle = 'white';
            let eyeOffsetX = 0;
            if (keys['KeyD'] || keys['ArrowRight']) eyeOffsetX = 5;
            if (keys['KeyA'] || keys['ArrowLeft']) eyeOffsetX = -5;
            
            ctx.beginPath();
            ctx.arc(player.x + player.width/2 - 5 + eyeOffsetX, player.y + player.height/2 - 5, 3, 0, Math.PI * 2);
            ctx.arc(player.x + player.width/2 + 5 + eyeOffsetX, player.y + player.height/2 - 5, 3, 0, Math.PI * 2);
            ctx.fill();
            
            // Draw player mouth
            ctx.beginPath();
            ctx.arc(player.x + player.width/2 + eyeOffsetX, player.y + player.height/2 + 5, 8, 0, Math.PI);
            ctx.strokeStyle = 'white';
            ctx.lineWidth = 2;
            ctx.stroke();
            
            // Draw score on canvas
            ctx.fillStyle = 'white';
            ctx.font = '20px Arial';
            ctx.fillText(\`Score: \${score}\`, 10, 30);
            ctx.fillText(\`Items: \${itemsCollected}/10\`, 10, 60);
        }
        
        function updateStats() {
            document.getElementById('score').textContent = score;
            document.getElementById('items').textContent = itemsCollected;
            document.getElementById('keys-pressed').textContent = keysPressedCount;
            document.getElementById('speed').textContent = (keys['ShiftLeft'] || keys['ShiftRight']) ? '10' : '5';
        }
        
        function resetGame() {
            initGame();
        }
        
        function togglePause() {
            // In a real game, you would pause the game loop
            alert('Game paused! Press OK to continue.');
        }
        
        // ========== CONTROL BUTTONS ==========
        document.getElementById('clear-log').addEventListener('click', function() {
            document.getElementById('key-log').innerHTML = '';
        });
        
        document.getElementById('start-log').addEventListener('click', function() {
            isLogging = true;
            logKeyEvent('system', { key: 'Logging', code: 'Started', ctrlKey: false, shiftKey: false, altKey: false, metaKey: false });
        });
        
        document.getElementById('stop-log').addEventListener('click', function() {
            isLogging = false;
            logKeyEvent('system', { key: 'Logging', code: 'Stopped', ctrlKey: false, shiftKey: false, altKey: false, metaKey: false });
        });
        
        // ========== GAME LOOP ==========
        function gameLoop() {
            updateGame();
            requestAnimationFrame(gameLoop);
        }
        
        // ========== INITIALIZE ==========
        initGame();
        gameLoop();
        
        // Initial log entry
        setTimeout(() => {
            logKeyEvent('system', { key: 'System', code: 'Ready', ctrlKey: false, shiftKey: false, altKey: false, metaKey: false });
        }, 1000);
    &lt;/script&gt;
