    &lt;script&gt;
        // ========== FORM ELEMENTS ==========
        const form = document.getElementById('registration-form');
        const usernameInput = document.getElementById('username');
        const emailInput = document.getElementById('email');
        const passwordInput = document.getElementById('password');
        const confirmInput = document.getElementById('confirm-password');
        const birthdateInput = document.getElementById('birthdate');
        const phoneInput = document.getElementById('phone');
        const countrySelect = document.getElementById('country');
        const termsCheckbox = document.getElementById('terms');
        const submitButton = document.getElementById('submit-btn');
        const togglePasswordBtn = document.getElementById('toggle-password');
        
        // ========== VALIDATION FUNCTIONS ==========
        function validateUsername() {
            const username = usernameInput.value.trim();
            const errorElement = document.getElementById('username-error');
            
            // Check if empty
            if (username.length === 0) {
                setValidation(usernameInput, false, 'Username is required');
                return false;
            }
            
            // Check length
            if (username.length < 3 || username.length > 20) {
                setValidation(usernameInput, false, 'Username must be 3-20 characters');
                return false;
            }
            
            // Check characters (alphanumeric and underscore only)
            const usernameRegex = /^[a-zA-Z0-9_]+$/;
            if (!usernameRegex.test(username)) {
                setValidation(usernameInput, false, 'Only letters, numbers, and underscores allowed');
                return false;
            }
            
            // Check for reserved usernames
            const reserved = ['admin', 'root', 'system', 'moderator'];
            if (reserved.includes(username.toLowerCase())) {
                setValidation(usernameInput, false, 'This username is not available');
                return false;
            }
            
            setValidation(usernameInput, true, '');
            return true;
        }
        
        function validateEmail() {
            const email = emailInput.value.trim();
            const errorElement = document.getElementById('email-error');
            
            if (email.length === 0) {
                setValidation(emailInput, false, 'Email is required');
                return false;
            }
            
            // Basic email validation
            const emailRegex = /^[^\\s@]+@[^\\s@]+\\.[^\\s@]+$/;
            if (!emailRegex.test(email)) {
                setValidation(emailInput, false, 'Please enter a valid email address');
                return false;
            }
            
            // Check for common disposable email domains
            const disposableDomains = ['tempmail.com', 'throwaway.com', 'fake.com'];
            const domain = email.split('@')[1];
            if (disposableDomains.includes(domain.toLowerCase())) {
                setValidation(emailInput, false, 'Disposable email addresses are not allowed');
                return false;
            }
            
            setValidation(emailInput, true, '');
            return true;
        }
        
        function validatePassword() {
            const password = passwordInput.value;
            const errorElement = document.getElementById('password-error');
            const strengthBar = document.getElementById('password-strength');
            
            if (password.length === 0) {
                setValidation(passwordInput, false, 'Password is required');
                strengthBar.className = 'strength-bar';
                return false;
            }
            
            // Check length
            if (password.length < 8) {
                setValidation(passwordInput, false, 'Password must be at least 8 characters');
                strengthBar.className = 'strength-bar strength-weak';
                return false;
            }
            
            // Calculate password strength
            let strength = 0;
            
            // Length check
            if (password.length >= 8) strength++;
            if (password.length >= 12) strength++;
            
            // Character variety checks
            if (/[a-z]/.test(password)) strength++; // lowercase
            if (/[A-Z]/.test(password)) strength++; // uppercase
            if (/[0-9]/.test(password)) strength++; // numbers
            if (/[^a-zA-Z0-9]/.test(password)) strength++; // special characters
            
            // Set strength bar
            if (strength <= 2) {
                setValidation(passwordInput, false, 'Password is weak');
                strengthBar.className = 'strength-bar strength-weak';
                return false;
            } else if (strength <= 4) {
                setValidation(passwordInput, true, 'Password is fair');
                strengthBar.className = 'strength-bar strength-fair';
            } else if (strength <= 5) {
                setValidation(passwordInput, true, 'Password is good');
                strengthBar.className = 'strength-bar strength-good';
            } else {
                setValidation(passwordInput, true, 'Password is strong!');
                strengthBar.className = 'strength-bar strength-strong';
            }
            
            return true;
        }
        
        function validateConfirmPassword() {
            const password = passwordInput.value;
            const confirm = confirmInput.value;
            const errorElement = document.getElementById('confirm-error');
            
            if (confirm.length === 0) {
                setValidation(confirmInput, false, 'Please confirm your password');
                return false;
            }
            
            if (password !== confirm) {
                setValidation(confirmInput, false, 'Passwords do not match');
                return false;
            }
            
            setValidation(confirmInput, true, '');
            return true;
        }
        
        function validateBirthdate() {
            const birthdate = new Date(birthdateInput.value);
            const errorElement = document.getElementById('birthdate-error');
            
            if (!birthdateInput.value) {
                setValidation(birthdateInput, false, 'Date of birth is required');
                return false;
            }
            
            // Calculate age
            const today = new Date();
            let age = today.getFullYear() - birthdate.getFullYear();
            const monthDiff = today.getMonth() - birthdate.getMonth();
            
            if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthdate.getDate())) {
                age--;
            }
            
            if (age < 13) {
                setValidation(birthdateInput, false, 'You must be at least 13 years old');
                return false;
            }
            
            if (age > 120) {
                setValidation(birthdateInput, false, 'Please enter a valid date');
                return false;
            }
            
            setValidation(birthdateInput, true, '');
            return true;
        }
        
        function validatePhone() {
            const phone = phoneInput.value.trim();
            const errorElement = document.getElementById('phone-error');
            
            // Phone is optional, so if empty, it's valid
            if (phone.length === 0) {
                setValidation(phoneInput, true, '');
                return true;
            }
            
            // Basic phone validation (supports multiple formats)
            const phoneRegex = /^[\+]?[1-9][\d]{0,15}$/;
            const cleanPhone = phone.replace(/[-\s()]/g, '');
            
            if (!phoneRegex.test(cleanPhone)) {
                setValidation(phoneInput, false, 'Please enter a valid phone number');
                return false;
            }
            
            setValidation(phoneInput, true, '');
            return true;
        }
        
        function validateCountry() {
            const country = countrySelect.value;
            const errorElement = document.getElementById('country-error');
            
            if (country === '') {
                setValidation(countrySelect, false, 'Please select your country');
                return false;
            }
            
            setValidation(countrySelect, true, '');
            return true;
        }
        
        function validateTerms() {
            const errorElement = document.getElementById('terms-error');
            
            if (!termsCheckbox.checked) {
                // Don't set validation on checkbox, just show error
                errorElement.textContent = 'You must agree to the terms';
                return false;
            }
            
            errorElement.textContent = '';
            return true;
        }
        
        function setValidation(element, isValid, message) {
            const errorId = element.id + '-error';
            const errorElement = document.getElementById(errorId);
            
            element.classList.remove('valid', 'invalid');
            
            if (isValid) {
                element.classList.add('valid');
                if (errorElement) errorElement.textContent = '';
            } else {
                element.classList.add('invalid');
                if (errorElement) errorElement.textContent = message;
            }
            
            updateSubmitButton();
        }
        
        function updateSubmitButton() {
            // Check all validations
            const isFormValid = 
                validateUsername() &&
                validateEmail() &&
                validatePassword() &&
                validateConfirmPassword() &&
                validateBirthdate() &&
                validatePhone() &&
                validateCountry() &&
                validateTerms();
            
            submitButton.disabled = !isFormValid;
        }
        
        // ========== EVENT LISTENERS ==========
        // Real-time validation on input
        usernameInput.addEventListener('input', validateUsername);
        emailInput.addEventListener('input', validateEmail);
        passwordInput.addEventListener('input', function() {
            validatePassword();
            validateConfirmPassword(); // Also validate confirmation when password changes
        });
        confirmInput.addEventListener('input', validateConfirmPassword);
        birthdateInput.addEventListener('change', validateBirthdate);
        phoneInput.addEventListener('input', validatePhone);
        countrySelect.addEventListener('change', validateCountry);
        termsCheckbox.addEventListener('change', validateTerms);
        
        // Validate on blur (when user leaves field)
        usernameInput.addEventListener('blur', validateUsername);
        emailInput.addEventListener('blur', validateEmail);
        passwordInput.addEventListener('blur', validatePassword);
        confirmInput.addEventListener('blur', validateConfirmPassword);
        birthdateInput.addEventListener('blur', validateBirthdate);
        phoneInput.addEventListener('blur', validatePhone);
        
        // Form submission
        form.addEventListener('submit', function(event) {
            event.preventDefault(); // Prevent page reload
            
            // Final validation
            updateSubmitButton();
            
            if (!submitButton.disabled) {
                // Form is valid - process submission
                processFormSubmission();
            }
        });
        
        // Toggle password visibility
        let isPasswordVisible = false;
        togglePasswordBtn.addEventListener('click', function() {
            isPasswordVisible = !isPasswordVisible;
            
            if (isPasswordVisible) {
                passwordInput.type = 'text';
