    &lt;script&gt;
        // ========== GLOBAL STATE ==========
        let currentKit = 'rock';
        let volume = 0.7;
        let isRecording = false;
        let isPlayingBack = false;
        let isSequencePlaying = false;
        let recordingStartTime = null;
        let recordingTimer = null;
        let recordedEvents = [];
        let playbackEvents = [];
        let totalPlays = 0;
        let savedBeats = [];
        let currentSequence = [];
        let sequencePosition = 0;
