                ]
            },
            
            experimental: {
                name: 'Experimental',
                description: 'Unique soundscape',
                sounds: [
                    { key: 'A', name: 'Glass Break', icon: 'ğŸ¥‚', color: '#4fd1c7' },
                    { key: 'S', name: 'Laser Zap', icon: 'ğŸ”«', color: '#f687b3' },
                    { key: 'D', name: 'Robot Beep', icon: 'ğŸ¤–', color: '#68d391' },
                    { key: 'F', name: 'Space Whoosh', icon: 'ğŸš€', color: '#63b3ed' },
                    { key: 'G', name: 'Bubble Pop', icon: 'ğŸ«§', color: '#fbb6ce' },
                    { key: 'H', name: 'Rain Drop', icon: 'ğŸŒ§ï¸', color: '#90cdf4' },
                    { key: 'J', name: 'Wind Chime', icon: 'ğŸ', color: '#faf089' },
                    { key: 'K', name: 'Thunder', icon: 'â›ˆï¸', color: '#b794f4' }
                ]
            }
        };
        
        // ========== AUDIO CONTEXT SETUP ==========
        const AudioContext = window.AudioContext || window.webkitAudioContext;
        let audioContext;
        let masterGain;
        
        function initAudio() {
            try {
                audioContext = new AudioContext();
                masterGain = audioContext.createGain();
                masterGain.connect(audioContext.destination);
                masterGain.gain.value = volume;
                
                console.log('Audio context initialized successfully');
            } catch (error) {
                console.error('Error initializing audio:', error);
                alert('Your browser does not support Web Audio API. Try Chrome or Firefox!');
            }
        }
        
        // ========== SOUND GENERATION ==========
        function playSound(drumIndex, velocity = 0.8) {
            if (!audioContext || audioContext.state === 'suspended') {
                audioContext.resume();
            }
            
            const drum = drumKits[currentKit].sounds[drumIndex];
            if (!drum) return;
            
            // Create audio buffer
            const buffer = audioContext.createBuffer(1, audioContext.sampleRate * 0.5, audioContext.sampleRate);
            const channelData = buffer.getChannelData(0);
            
            // Generate different sounds based on drum type
            let frequency;
            let decay;
            
            switch(drum.name.toLowerCase()) {
                case 'kick drum':
                case '808 kick':
                case 'boom bap':
                    frequency = 100;
                    decay = 0.5;
                    break;
                case 'snare drum':
                case 'clap':
                case 'snare snap':
                    frequency = 200;
                    decay = 0.3;
                    break;
                case 'hi-hat closed':
                case 'hi-hat':
                case 'hi-hat roll':
                    frequency = 1000;
                    decay = 0.1;
                    break;
                case 'hi-hat open':
                case 'open hat':
                    frequency = 800;
                    decay = 0.4;
                    break;
                case 'tom high':
                case 'snap':
                case 'vocal hit':
                    frequency = 300;
                    decay = 0.4;
                    break;
                case 'tom mid':
                case 'rimshot':
                case 'synth tom':
                    frequency = 200;
                    decay = 0.5;
                    break;
                case 'tom low':
                case 'cowbell':
                case 'bass drop':
                    frequency = 150;
                    decay = 0.6;
                    break;
                case 'crash cymbal':
                case 'cymbal':
                case 'fx sweep':
                    frequency = 500;
                    decay = 1.0;
                    break;
                default:
                    frequency = 440;
                    decay = 0.3;
            }
            
            // Generate sound
            for (let i = 0; i < buffer.length; i++) {
                const time = i / audioContext.sampleRate;
                const envelope = Math.exp(-time * 5 / decay);
                
                // Different waveforms for different sounds
                let sample;
                if (drum.name.includes('Hi-Hat') || drum.name.includes('Cymbal')) {
                    // Noise for hi-hats/cymbals
                    sample = (Math.random() * 2 - 1) * envelope;
                } else if (drum.name.includes('Snare') || drum.name.includes('Clap')) {
                    // Mix of tone and noise for snares
                    const tone = Math.sin(2 * Math.PI * frequency * time) * envelope;
                    const noise = (Math.random() * 2 - 1) * envelope * 0.7;
                    sample = tone * 0.3 + noise * 0.7;
                } else {
                    // Sine wave for kicks/toms
                    sample = Math.sin(2 * Math.PI * frequency * time) * envelope;
                }
                
                channelData[i] = sample * velocity;
            }
            
            // Play the sound
            const source = audioContext.createBufferSource();
            source.buffer = buffer;
            
            // Apply volume
            const gainNode = audioContext.createGain();
            gainNode.gain.value = volume;
            
            source.connect(gainNode);
            gainNode.connect(masterGain);
            source.start();
            
            // Clean up
            source.onended = () => {
                source.disconnect();
                gainNode.disconnect();
            };
            
            // Record the event if recording is active
            if (isRecording && recordingStartTime) {
                const elapsedTime = Date.now() - recordingStartTime;
                recordedEvents.push({
                    time: elapsedTime,
                    drumIndex: drumIndex,
                    velocity: velocity,
                    drumName: drum.name
                });
            }
            
            // Update stats
            totalPlays++;
            updateStats();
            
            // Visual feedback
            animateDrumPad(drumIndex);
            updateVisualizer(drumIndex);
            
            return source;
        }
        
        // ========== DRUM PAD SETUP ==========
        function createDrumPads() {
            const drumGrid = document.getElementById('drum-grid');
            drumGrid.innerHTML = '';
            
            const kit = drumKits[currentKit];
            
            kit.sounds.forEach((drum, index) => {
                const drumPad = document.createElement('div');
                drumPad.className = 'drum-pad';
                drumPad.dataset.index = index;
                drumPad.dataset.key = drum.key;
                drumPad.style.borderColor = drum.color;
                
                drumPad.innerHTML = \`
                    <div class=\"drum-icon\" style=\"color: \${drum.color}\">\${drum.icon}</div>
                    <div class=\"drum-key\" style=\"color: \${drum.color}\">\${drum.key}</div>
                    <div class=\"drum-name\">\${drum.name}</div>
                \`;
                
                // Click event
                drumPad.addEventListener('click', () => {
                    playSound(index, 0.8);
                });
                
                // Touch events for mobile
                drumPad.addEventListener('touchstart', (e) => {
                    e.preventDefault();
                    playSound(index, 0.8);
                    drumPad.classList.add('active');
                });
                
                drumPad.addEventListener('touchend', () => {
                    setTimeout(() => drumPad.classList.remove('active'), 100);
                });
                
                drumGrid.appendChild(drumPad);
            });
        }
        
        function animateDrumPad(drumIndex) {
            const drumPad = document.querySelector(\`.drum-pad[data-index=\"\${drumIndex}\"]\`);
            if (!drumPad) return;
            
            drumPad.classList.add('active');
            
            // Add ripple effect
            const ripple = document.createElement('div');
            ripple.style.position = 'absolute';
            ripple.style.width = '100%';
            ripple.style.height = '100%';
            ripple.style.borderRadius = '15px';
            ripple.style.background = 'radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%)';
            ripple.style.animation = 'ripple 0.5s ease-out';
            drumPad.appendChild(ripple);
            
            // Remove animation class and ripple after animation
            setTimeout(() => {
                drumPad.classList.remove('active');
                if (ripple.parentNode === drumPad) {
                    drumPad.removeChild(ripple);
                }
            }, 100);
        }
        
        // ========== KEYBOARD CONTROLS ==========
        const keyToDrumIndex = {
            'KeyA': 0, 'KeyS': 1, 'KeyD': 2, 'KeyF': 3,
            'KeyG': 4, 'KeyH': 5, 'KeyJ': 6, 'KeyK': 7
        };
        
        let activeKeys = new Set();
        
        document.addEventListener('keydown', (event) => {
            // Prevent default for our drum keys
            if (keyToDrumIndex.hasOwnProperty(event.code)) {
                event.preventDefault();
                
                // Only play if key wasn't already pressed (avoid repeated firing)
                if (!activeKeys.has(event.code)) {
                    activeKeys.add(event.code);
                    const drumIndex = keyToDrumIndex[event.code];
                    playSound(drumIndex, 0.8);
                }
            }
            
            // Spacebar to start/stop recording
            if (event.code === 'Space' && !event.repeat) {
                event.preventDefault();
                toggleRecording();
            }
            
            // Enter to play recording
            if (event.code === 'Enter' && !event.repeat) {
                event.preventDefault();
                playRecording();
            }
            
            // Number keys 1-4 to switch kits
            if (event.code >= 'Digit1' && event.code <= 'Digit4') {
                const kitIndex = parseInt(event.code[5]) - 1;
                const kitNames = Object.keys(drumKits);
                if (kitNames[kitIndex]) {
                    switchKit(kitNames[kitIndex]);
                }
            }
        });
        
        document.addEventListener('keyup', (event) => {
            if (keyToDrumIndex.hasOwnProperty(event.code)) {
                activeKeys.delete(event.code);
            }
        });
        
        // ========== KIT SELECTOR ==========
        function createKitSelector() {
            const kitSelector = document.getElementById('kit-selector');
            kitSelector.innerHTML = '';
            
            Object.keys(drumKits).forEach((kitId, index) => {
                const kit = drumKits[kitId];
                const kitOption = document.createElement('div');
                kitOption.className = \`kit-option \${kitId === currentKit ? 'active' : ''}\`;
                kitOption.dataset.kit = kitId;
                kitOption.innerHTML = \`
                    <div class=\"kit-name\">\${kit.name}</div>
                    <div class=\"kit-desc\">\${kit.description}</div>
                    <div style=\"margin-top: 5px; font-size: 0.8em; color: #90cdf4;\">
                        Press <kbd style=\"background: rgba(255,255,255,0.1); padding: 2px 6px; border-radius: 3px;\">\${index + 1}</kbd>
                    </div>
                \`;
                
                kitOption.addEventListener('click', () => switchKit(kitId));
                kitSelector.appendChild(kitOption);
            });
        }
        
        function switchKit(kitId) {
            if (drumKits[kitId]) {
                currentKit = kitId;
                createDrumPads();
                createKitSelector();
                updateStats();
                
                // Visual feedback
                document.getElementById('active-kit').textContent = drumKits[kitId].name;
                document.getElementById('active-kit').style.animation = 'glow 0.5s';
                setTimeout(() => {
                    document.getElementById('active-kit').style.animation = '';
                }, 500);
            }
        }
        
        // ========== VOLUME CONTROL ==========
        function setupVolumeControl() {
            const volumeSlider = document.getElementById('volume-slider');
            const volumeValue = document.getElementById('volume-value');
            
            volumeSlider.addEventListener('input', () => {
                volume = volumeSlider.value / 100;
                if (masterGain) {
                    masterGain.gain.value = volume;
                }
                volumeValue.textContent = \`\${volumeSlider.value}%\`;
            });
        }
        
        // ========== RECORDING FUNCTIONALITY ==========
        function toggleRecording() {
            if (!isRecording) {
                // Start recording
                isRecording = true;
                recordedEvents = [];
                recordingStartTime = Date.now();
                
                // Update UI
                document.getElementById('record-btn').classList.add('recording');
                document.getElementById('record-btn').innerHTML = '<i class=\"fas fa-stop\"></i> Stop';
                document.getElementById('recording-status').textContent = 'Recording...';
                document.getElementById('playback-btn').disabled = true;
                document.getElementById('clear-recording').disabled = true;
                document.getElementById('save-beat').disabled = true;
                
                // Start timer
                recordingTimer = setInterval(updateRecordingTimer, 1000);
                
                // Visual feedback
                document.title = 'â— Ultimate Web Drum Kit (Recording...)';
            } else {
                // Stop recording
                stopRecording();
            }
        }
        
        function stopRecording() {
            isRecording = false;
            
            // Update UI
            document.getElementById('record-btn').classList.remove('recording');
            document.getElementById('record-btn').innerHTML = '<i class=\"fas fa-circle\"></i> Record';
            document.getElementById('recording-status').textContent = \`Recorded \${recordedEvents.length} events\`;
            document.getElementById('playback-btn').disabled = false;
            document.getElementById('clear-recording').disabled = false;
            document.getElementById('save-beat').disabled = recordedEvents.length === 0;
            
            // Stop timer
            clearInterval(recordingTimer);
            document.getElementById('recording-timer').textContent = '00:00';
            
            // Reset title
            document.title = 'Ultimate Web Drum Kit';
            
            console.log('Recording saved with', recordedEvents.length, 'events');
        }
        
        function updateRecordingTimer() {
            if (!recordingStartTime) return;
            
            const elapsedSeconds = Math.floor((Date.now() - recordingStartTime) / 1000);
            const minutes = Math.floor(elapsedSeconds / 60).toString().padStart(2, '0');
            const seconds = (elapsedSeconds % 60).toString().padStart(2, '0');
            
            document.getElementById('recording-timer').textContent = \`\${minutes}:\${seconds}\`;
        }
        
        function playRecording() {
            if (recordedEvents.length === 0 || isPlayingBack) return;
            
            isPlayingBack = true;
            document.getElementById('playback-btn').disabled = true;
            document.getElementById('record-btn').disabled = true;
            document.getElementById('recording-status').textContent = 'Playing back...';
            
            // Create a copy for playback
            playbackEvents = [...recordedEvents];
            
            // Play first event
            playNextRecordedEvent();
        }
        
        function playNextRecordedEvent() {
            if (playbackEvents.length === 0) {
                // Finished playback
                isPlayingBack = false;
                document.getElementById('playback-btn').disabled = false;
                document.getElementById('record-btn').disabled = false;
                document.getElementById('recording-status').textContent = \`Recorded \${recordedEvents.length} events\`;
                return;
            }
            
            const event = playbackEvents.shift();
            const nextEvent = playbackEvents[0];
            
            // Play this event
            playSound(event.drumIndex, event.velocity);
            
            // Schedule next event
            if (nextEvent) {
                const delay = nextEvent.time - event.time;
                setTimeout(playNextRecordedEvent, delay);
            } else {
                // Last event
                setTimeout(() => {
                    isPlayingBack = false;
                    document.getElementById('playback-btn').disabled = false;
                    document.getElementById('record-btn').disabled = false;
                    document.getElementById('recording-status').textContent = \`Recorded \${recordedEvents.length} events\`;
                }, 1000);
            }
        }
        
        function clearRecording() {
            if (confirm('Are you sure you want to clear the recording?')) {
                recordedEvents = [];
                document.getElementById('recording-status').textContent = 'Ready to record!';
                document.getElementById('playback-btn').disabled = true;
                document.getElementById('clear-recording').disabled = true;
                document.getElementById('save-beat').disabled = true;
            }
        }
        
        function saveBeat() {
            if (recordedEvents.length === 0) return;
            
            const beatName = prompt('Name your beat:', \`Beat \${savedBeats.length + 1}\`);
            if (!beatName) return;
            
            const beat = {
                id: Date.now(),
                name: beatName,
                kit: currentKit,
                events: [...recordedEvents],
                timestamp: new Date().toLocaleString(),
                length: recordedEvents.length
            };
            
            savedBeats.push(beat);
            updateSavedBeats();
            updateStats();
            
            alert(\`Beat "\${beatName}" saved successfully!\`);
        }
        
        function updateSavedBeats() {
            const savedBeatsContainer = document.getElementById('saved-beats');
            
            if (savedBeats.length === 0) {
                savedBeatsContainer.innerHTML = \`
                    <div style=\"text-align: center; padding: 20px; color: #718096;\">
                        <i class=\"fas fa-inbox\" style=\"font-size: 2em; margin-bottom: 10px; display: block;\"></i>
                        No saved beats yet. Record something amazing!
                    </div>
                \`;
                return;
            }
            
            savedBeatsContainer.innerHTML = savedBeats.map(beat => \`
                <div style=\"background: rgba(255,255,255,0.05); padding: 15px; border-radius: 10px; margin-bottom: 10px;\">
                    <div style=\"display: flex; justify-content: space-between; align-items: center; margin-bottom: 10px;\">
                        <div>
                            <strong style=\"color: #63b3ed;\">\${beat.name}</strong>
                            <div style=\"font-size: 0.8em; color: #cbd5e0;\">
                                \${beat.timestamp} â€¢ \${beat.kit} Kit â€¢ \${beat.length} events
                            </div>
                        </div>
                        <div style=\"display: flex; gap: 5px;\">
                            <button onclick=\"playSavedBeat(\${beat.id})\" style=\"background: #3182ce; border: none; color: white; padding: 5px 10px; border-radius: 5px; cursor: pointer;\">
                                <i class=\"fas fa-play\"></i>
                            </button>
                            <button onclick=\"deleteBeat(\${beat.id})\" style=\"background: #e53e3e; border: none; color: white; padding: 5px 10px; border-radius: 5px; cursor: pointer;\">
                                <i class=\"fas fa-trash\"></i>
                            </button>
                        </div>
                    </div>
                </div>
            \`).join('');
        }
        
        function playSavedBeat(beatId) {
            const beat = savedBeats.find(b => b.id === beatId);
            if (!beat) return;
            
            // Switch to the kit used in the recording
            switchKit(beat.kit);
            
            // Load the events and play them
            recordedEvents = [...beat.events];
            setTimeout(() => playRecording(), 500);
        }
        
        function deleteBeat(beatId) {
            if (confirm('Are you sure you want to delete this beat?')) {
                savedBeats = savedBeats.filter(b => b.id !== beatId);
                updateSavedBeats();
                updateStats();
            }
        }
        
        // ========== BEAT SEQUENCER ==========
        function createBeatSequencer() {
            const beatGrid = document.getElementById('beat-grid');
            beatGrid.innerHTML = '';
            
            // Create 16x8 grid (16 beats, 8 drums)
            for (let beat = 0; beat < 16; beat++) {
                for (let drum = 0; drum < 8; drum++) {
                    const cell = document.createElement('div');
                    cell.className = 'beat-cell';
                    cell.dataset.beat = beat;
                    cell.dataset.drum = drum;
                    
                    // Initialize sequence array
                    if (!currentSequence[beat]) currentSequence[beat] = [];
                    currentSequence[beat][drum] = false;
                    
                    // Click to toggle
                    cell.addEventListener('click', () => {
                        currentSequence[beat][drum] = !currentSequence[beat][drum];
                        cell.classList.toggle('active', currentSequence[beat][drum]);
                    });
                    
                    beatGrid.appendChild(cell);
                }
            }
        }
        
        function playSequence() {
            if (isSequencePlaying) {
                stopSequence();
                return;
            }
            
            isSequencePlaying = true;
            sequencePosition = 0;
            document.getElementById('play-sequence').innerHTML = '<i class=\"fas fa-stop\"></i> Stop';
            
            // Clear previous highlights
            document.querySelectorAll('.beat-cell.current').forEach(cell => {
                cell.classList.remove('current');
            });
            
            // Play sequence
            sequenceInterval = setInterval(() => {
                // Highlight current beat
                document.querySelectorAll(\`.beat-cell[data-beat=\"\${sequencePosition}\"]\`).forEach(cell => {
                    cell.classList.add('current');
                });
                
                // Remove highlight from previous beat
                const prevBeat = (sequencePosition - 1 + 16) % 16;
                document.querySelectorAll(\`.beat-cell[data-beat=\"\${prevBeat}\"]\`).forEach(cell => {
                    cell.classList.remove('current');
                });
                
                // Play sounds for this beat
                if (currentSequence[sequencePosition]) {
                    currentSequence[sequencePosition].forEach((active, drumIndex) => {
                        if (active) {
                            playSound(drumIndex, 0.6);
                        }
                    });
                }
                
                // Move to next beat
                sequencePosition = (sequencePosition + 1) % 16;
            }, 60000 / parseInt(document.getElementById('current-bpm').textContent)); // Based on BPM
        }
        
        function stopSequence() {
            isSequencePlaying = false;
            clearInterval(sequenceInterval);
            document.getElementById('play-sequence').innerHTML = '<i class=\"fas fa-play\"></i> Play Sequence';
            
            // Clear highlights
            document.querySelectorAll('.beat-cell.current').forEach(cell => {
                cell.classList.remove('current');
            });
        }
        
        function clearSequence() {
            if (confirm('Clear the entire sequence?')) {
                currentSequence = [];
                createBeatSequencer();
            }
        }
        
        // ========== VISUALIZER ==========
        function updateVisualizer(drumIndex) {
            const canvas = document.getElementById('visualizer');
            const ctx = canvas.getContext('2d');
            
            if (!canvas.width) {
                canvas.width = canvas.offsetWidth;
                canvas.height = canvas.offsetHeight;
            }
            
            // Clear canvas with fade effect
            ctx.fillStyle = 'rgba(0, 0, 0, 0.1)';
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            
            // Get drum color
            const drum = drumKits[currentKit].sounds[drumIndex];
            const color = drum ? drum.color : '#63b3ed';
            
            // Draw waveform based on drum index
            const centerY = canvas.height / 2;
            const amplitude = canvas.height * 0.4;
            const frequency = 0.05;
            
            ctx.beginPath();
            ctx.strokeStyle = color;
            ctx.lineWidth = 3;
            
            for (let x = 0; x < canvas.width; x++) {
                const time = (x + Date.now() * 0.01) * frequency;
                const drumFactor = (drumIndex + 1) * 0.5;
                const y = centerY + Math.sin(time * drumFactor) * amplitude * Math.exp(-x / canvas.width * 3);
                
                if (x === 0) {
                    ctx.moveTo(x, y);
                } else {
                    ctx.lineTo(x, y);
                }
            }
            
            ctx.stroke();
            
            // Draw circles for each drum hit
            const now = Date.now();
            for (let i = 0; i < 5; i++) {
                const age = now - (i * 200);
                if (age < 1000) {
                    const size = (1 - age / 1000) * 20;
                    const alpha = 1 - age / 1000;
                    
                    ctx.beginPath();
                    ctx.arc(
                        canvas.width - 50 - i * 30,
                        centerY,
                        size,
                        0,
                        Math.PI * 2
                    );
                    ctx.fillStyle = \`rgba(\${parseInt(color.slice(1, 3), 16)}, \${parseInt(color.slice(3, 5), 16)}, \${parseInt(color.slice(5, 7), 16)}, \${alpha})\`;
                    ctx.fill();
                }
            }
        }
        
        // ========== STATS UPDATER ==========
        function updateStats() {
            document.getElementById('total-plays').textContent = totalPlays;
            document.getElementById('active-kit').textContent = drumKits[currentKit].name;
            document.getElementById('memory-usage').textContent = savedBeats.length;
        }
        
        // ========== INITIALIZATION ==========
        function init() {
            // Initialize audio
            initAudio();
            
            // Create drum pads
            createDrumPads();
            
            // Create kit selector
            createKitSelector();
            
            // Setup volume control
            setupVolumeControl();
            
            // Create beat sequencer
            createBeatSequencer();
            
            // Setup control buttons
            document.getElementById('record-btn').addEventListener('click', toggleRecording);
            document.getElementById('playback-btn').addEventListener('click', playRecording);
            document.getElementById('clear-recording').addEventListener('click', clearRecording);
            document.getElementById('save-beat').addEventListener('click', saveBeat);
            document.getElementById('play-sequence').addEventListener('click', playSequence);
            document.getElementById('clear-sequence').addEventListener('click', clearSequence);
            
            // Setup BPM control
            const bpmElement = document.getElementById('current-bpm');
            bpmElement.addEventListener('click', () => {
                const newBPM = prompt('Enter BPM (beats per minute):', bpmElement.textContent);
                if (newBPM && !isNaN(newBPM) && newBPM >= 40 && newBPM <= 240) {
                    bpmElement.textContent = Math.round(newBPM);
                    if (isSequencePlaying) {
                        stopSequence();
                        playSequence();
                    }
                }
            });
            
            // Initial stats
            updateStats();
            
            // Add CSS for ripple animation
            const style = document.createElement('style');
            style.textContent = \`
                @keyframes ripple {
                    0% {
                        transform: scale(0.8);
                        opacity: 1;
                    }
                    100% {
                        transform: scale(2);
                        opacity: 0;
                    }
                }
                
                .beat-cell {
                    transition: background 0.2s, transform 0.2s;
                }
                
                .beat-cell:hover {
                    transform: scale(1.1);
                }
            \`;
            document.head.appendChild(style);
            
            // Initialize with a welcome sound
            setTimeout(() => {
                if (audioContext) {
                    playSound(0, 0.5);
                    setTimeout(() => playSound(1, 0.5), 200);
                    setTimeout(() => playSound(2, 0.5), 400);
                }
            }, 1000);
            
            console.log('Drum Kit initialized successfully!');
        }
        
        // ========== START THE APP ==========
        // Wait for user interaction to start audio (browser requirement)
        document.addEventListener('click', () => {
            if (!audioContext) {
                init();
            }
        }, { once: true });
        
        // Also start on keypress
        document.addEventListener('keydown', () => {
            if (!audioContext) {
                init();
            }
        }, { once: true });
        
        // If page loads with a click or keypress already, initialize
        if (document.readyState === 'complete') {
            setTimeout(init, 500);
        } else {
            window.addEventListener('load', () => setTimeout(init, 500));
        }
