        let room = rooms[this.currentRoom];
        let itemIndex = room.items.indexOf(itemName);
        
        if (itemIndex > -1) {
            room.items.splice(itemIndex, 1);
            this.inventory.push(itemName);
            this.score += 10;
            return \`You took the \${itemName}\`;
        } else {
