// JavaScript
let canvas = document.getElementById("gameCanvas");
let ctx = canvas.getContext("2d");

// Ball properties
let ball = {
    x: 50,
    y: 200,
    radius: 20,
    speedX: 3,
    speedY: 2,
    color: "#FF6B6B"
};

// Animation variables
let animationId = null;
let isAnimating = false;

// Draw the ball
function drawBall() {
    ctx.beginPath();
    ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
    ctx.fillStyle = ball.color;
    ctx.fill();
    ctx.strokeStyle = "#FF5252";
    ctx.lineWidth = 3;
    ctx.stroke();
}

// Update ball position
function updateBall() {
    // Move the ball
    ball.x += ball.speedX;
    ball.y += ball.speedY;
    
    // Bounce off walls
    if (ball.x + ball.radius > canvas.width || 
        ball.x - ball.radius < 0) {
        ball.speedX = -ball.speedX;
        ball.color = getRandomColor();
    }
    
    if (ball.y + ball.radius > canvas.height || 
        ball.y - ball.radius < 0) {
        ball.speedY = -ball.speedY;
        ball.color = getRandomColor();
    }
}

// Main animation loop
function animate() {
    // Clear the canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    // Update and draw
    updateBall();
    drawBall();
    
    // Draw trail effect
    drawTrail();
    
    // Continue animation
    if (isAnimating) {
        animationId = requestAnimationFrame(animate);
    }
}

// Start animation
function startAnimation() {
    if (!isAnimating) {
        isAnimating = true;
        animate();
    }
}

// Stop animation
function stopAnimation() {
    isAnimating = false;
    if (animationId) {
        cancelAnimationFrame(animationId);
    }
}

// Helper: Get random color
function getRandomColor() {
    let colors = ["#FF6B6B", "#4ECDC4", "#FFD166", 
                  "#06D6A0", "#118AB2", "#EF476F"];
    return colors[Math.floor(Math.random() * colors.length)];
}

// Draw trail behind ball
function drawTrail() {
    for (let i = 0; i < 5; i++) {
        let trailX = ball.x - (ball.speedX * i * 3);
        let trailY = ball.y - (ball.speedY * i * 3);
        let trailRadius = ball.radius * (1 - i * 0.2);
        let opacity = 1 - (i * 0.2);
        
        ctx.beginPath();
        ctx.arc(trailX, trailY, trailRadius, 0, Math.PI * 2);
        ctx.fillStyle = \`rgba(255, 107, 107, \${opacity})\`;
        ctx.fill();
    }
}
