// FPS Counter
let frameCount = 0;
let lastFpsUpdate = 0;
let fps = 0;

function updateFPS(timestamp) {
    frameCount++;
    
    if (timestamp >= lastFpsUpdate + 1000) {
        fps = frameCount;
        frameCount = 0;
        lastFpsUpdate = timestamp;
        
        // Update display
        document.getElementById("fpsCounter").textContent = fps;
    }
}

// Frame time monitoring
let lastFrameTime = performance.now();
let frameTimes = [];

function monitorPerformance() {
    let now = performance.now();
    let frameTime = now - lastFrameTime;
    lastFrameTime = now;
    
    frameTimes.push(frameTime);
    if (frameTimes.length > 60) frameTimes.shift();
    
    // Calculate average frame time
    let avgFrameTime = frameTimes.reduce((a, b) => a + b) / 
                       frameTimes.length;
    
    if (avgFrameTime > 16.7) { // Less than 60 FPS
        console.warn("Performance drop detected!");
    }
}

// Memory monitoring
function checkMemory() {
    if (performance.memory) {
        let usedMB = performance.memory.usedJSHeapSize / 1048576;
        let totalMB = performance.memory.totalJSHeapSize / 1048576;
        
        if (usedMB > totalMB * 0.8) {
            console.warn("High memory usage!");
        }
    }
}
