// Rectangle collision detection
function rectCollision(rect1, rect2) {
    return (
        rect1.x < rect2.x + rect2.width &&
        rect1.x + rect1.width > rect2.x &&
        rect1.y < rect2.y + rect2.height &&
        rect1.y + rect1.height > rect2.y
    );
}

// More efficient version (pre-calculated bounds)
function optimizedRectCollision(rect1, rect2) {
    let rect1Right = rect1.x + rect1.width;
    let rect1Bottom = rect1.y + rect1.height;
    let rect2Right = rect2.x + rect2.width;
    let rect2Bottom = rect2.y + rect2.height;
    
    return (
        rect1.x < rect2Right &&
        rect1Right > rect2.x &&
        rect1.y < rect2Bottom &&
        rect1Bottom > rect2.y
    );
}

// Example usage
let player = { x: 100, y: 150, width: 50, height: 50 };
let enemy = { x: 130, y: 170, width: 50, height: 50 };

if (rectCollision(player, enemy)) {
    console.log("ğŸ’¥ Collision detected!");
    player.health -= 10;
