// Collision resolution (what happens AFTER collision)
function resolveCollision(rect1, rect2) {
    // Calculate overlap on each axis
    let overlapX = Math.min(
        rect1.x + rect1.width - rect2.x,
        rect2.x + rect2.width - rect1.x
    );
    
    let overlapY = Math.min(
        rect1.y + rect1.height - rect2.y,
        rect2.y + rect2.height - rect1.y
    );
    
    // Resolve based on smallest overlap
    if (overlapX < overlapY) {
        if (rect1.x < rect2.x) {
            rect1.x -= overlapX / 2;
            rect2.x += overlapX / 2;
        } else {
            rect1.x += overlapX / 2;
            rect2.x -= overlapX / 2;
        }
    } else {
        if (rect1.y < rect2.y) {
            rect1.y -= overlapY / 2;
            rect2.y += overlapY / 2;
        } else {
            rect1.y += overlapY / 2;
            rect2.y -= overlapY / 2;
        }
    }
    
    // Bounce effect (optional)
    let tempSpeedX = rect1.speedX;
    let tempSpeedY = rect1.speedY;
    rect1.speedX = rect2.speedX;
    rect1.speedY = rect2.speedY;
    rect2.speedX = tempSpeedX;
    rect2.speedY = tempSpeedY;
}

// Grid-based collision optimization
class CollisionGrid {
    constructor(cellSize) {
        this.cellSize = cellSize;
        this.grid = new Map();
    }
    
    clear() {
        this.grid.clear();
    }
    
    addObject(obj) {
        let cellX = Math.floor(obj.x / this.cellSize);
        let cellY = Math.floor(obj.y / this.cellSize);
        let key = \`\${cellX},\${cellY}\`\;
        
        if (!this.grid.has(key)) {
            this.grid.set(key, []);
        }
        this.grid.get(key).push(obj);
        
        // Store which cells object is in
        obj.cells = obj.cells || [];
        obj.cells.push(key);
    }
    
    getNearbyObjects(obj) {
        let nearby = [];
        let cellX = Math.floor(obj.x / this.cellSize);
        let cellY = Math.floor(obj.y / this.cellSize);
        
        // Check 3x3 grid around object
        for (let dx = -1; dx <= 1; dx++) {
            for (let dy = -1; dy <= 1; dy++) {
                let key = \`\${cellX + dx},\${cellY + dy}\`;
                if (this.grid.has(key)) {
                    nearby.push(...this.grid.get(key));
                }
            }
        }
        
        return nearby;
    }
}

