// Separating Axis Theorem (SAT) for polygons
function satCollision(poly1, poly2) {
    let axes = getAxes(poly1).concat(getAxes(poly2));
    
    for (let axis of axes) {
        let proj1 = projectPolygon(poly1, axis);
        let proj2 = projectPolygon(poly2, axis);
        
        if (!overlap(proj1, proj2)) {
            return false; // No collision
        }
    }
    
    return true; // Collision on all axes
}

// Ray casting for line-of-sight
function rayCast(startX, startY, endX, endY, obstacles) {
    let dx = endX - startX;
    let dy = endY - startY;
    let length = Math.sqrt(dx * dx + dy * dy);
    
    // Normalize direction
    dx /= length;
    dy /= length;
    
    // Step along ray
    for (let i = 0; i < length; i += 5) {
        let x = startX + dx * i;
        let y = startY + dy * i;
        
        for (let obstacle of obstacles) {
            if (pointInRect(x, y, obstacle)) {
                return false; // Line of sight blocked
            }
        }
    }
    
    return true; // Clear line of sight
}

// Continuous collision detection (for fast objects)
function continuousCollision(obj1, obj2, dt) {
    // Predict future positions
    let futureX1 = obj1.x + obj1.speedX * dt;
    let futureY1 = obj1.y + obj1.speedY * dt;
    let futureX2 = obj2.x + obj2.speedX * dt;
    let futureY2 = obj2.y + obj2.speedY * dt;
    
    // Check if paths intersect
    return linesIntersect(
        obj1.x, obj1.y, futureX1, futureY1,
        obj2.x, obj2.y, futureX2, futureY2
    );
}
