// Grid constants
const GRID_SIZE = 20; // 20px per grid cell
const GRID_WIDTH = 30; // 30 cells wide
const GRID_HEIGHT = 20; // 20 cells tall

// Snake represented as array of grid positions
let snake = [
    {x: 10, y: 10}, // Head
    {x: 9, y: 10},  // Body segment 1
    {x: 8, y: 10}   // Body segment 2
];

// Food position (random grid cell)
let food = {
    x: Math.floor(Math.random() * GRID_WIDTH),
    y: Math.floor(Math.random() * GRID_HEIGHT)
};

// Convert grid position to pixel position
function gridToPixel(gridX, gridY) {
    return {
        x: gridX * GRID_SIZE,
        y: gridY * GRID_SIZE
    };
}

// Draw grid background
function drawGrid() {
    ctx.strokeStyle = "#34495e";
    ctx.lineWidth = 1;
    
    // Vertical lines
    for (let x = 0; x <= canvas.width; x += GRID_SIZE) {
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
    }
    
    // Horizontal lines
    for (let y = 0; y <= canvas.height; y += GRID_SIZE) {
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(canvas.width, y);
        ctx.stroke();
    }
}

// Draw snake on grid
function drawSnake() {
    for (let i = 0; i < snake.length; i++) {
        let segment = snake[i];
        let pixelPos = gridToPixel(segment.x, segment.y);
        
        // Head is different color
        if (i === 0) {
            ctx.fillStyle = "#2ECC71"; // Green head
        } else {
            // Gradient body
            let intensity = 200 - (i * 5);
            ctx.fillStyle = \`rgb(46, 204, \${intensity})\`;
        }
        
        // Draw segment with rounded corners
        ctx.beginPath();
        ctx.roundRect(
            pixelPos.x, 
            pixelPos.y, 
            GRID_SIZE, 
            GRID_SIZE, 
            5
        );
        ctx.fill();
        
        // Draw border
        ctx.strokeStyle = "#27AE60";
        ctx.lineWidth = 2;
        ctx.stroke();
    }
}
