class SnakeGame {
    constructor(canvasId) {
        this.canvas = document.getElementById(canvasId);
        this.ctx = this.canvas.getContext("2d");
        
        // Game settings
        this.gridSize = 20;
        this.gridWidth = Math.floor(this.canvas.width / this.gridSize);
        this.gridHeight = Math.floor(this.canvas.height / this.gridSize);
        
        // Game state
        this.reset();
        
        // Controls
        this.keyHandler = this.handleKey.bind(this);
        document.addEventListener("keydown", this.keyHandler);
        
        // Start game loop
        this.lastUpdate = 0;
        this.gameLoop = this.loop.bind(this);
        requestAnimationFrame(this.gameLoop);
    }
    
    reset() {
        // Snake starts with 3 segments in middle
        this.snake = [
            {x: 10, y: 10},
            {x: 9, y: 10},
            {x: 8, y: 10}
        ];
        
        // Game state
        this.score = 0;
        this.highScore = localStorage.getItem("snakeHighScore") || 0;
        this.direction = "right";
        this.nextDirection = "right";
        this.speed = 150; // ms per move
        this.foodEaten = 0;
        this.isPaused = false;
        this.isGameOver = false;
        
        // Generate first food
        this.generateFood();
        
        // Effects
        this.particles = [];
        this.effects = [];
    }
    
    generateFood() {
        let newFood;
        let onSnake;
        
        do {
            onSnake = false;
            newFood = {
                x: Math.floor(Math.random() * this.gridWidth),
                y: Math.floor(Math.random() * this.gridHeight),
                type: this.getRandomFoodType()
            };
            
            // Check if on snake
            for (let segment of this.snake) {
                if (segment.x === newFood.x && segment.y === newFood.y) {
                    onSnake = true;
                    break;
                }
            }
        } while (onSnake);
        
        this.food = newFood;
    }
    
    getRandomFoodType() {
        let types = [
            {color: "#FF6B6B", value: 10},   // Red apple
            {color: "#FFD166", value: 20},   // Yellow banana
            {color: "#06D6A0", value: 30},   // Green pear
            {color: "#118AB2", value: 50},   // Blue special
            {color: "#EF476F", value: 100}   // Pink bonus
        ];
        
        // Weighted random - common foods more likely
        let weights = [40, 30, 20, 7, 3];
        let totalWeight = weights.reduce((a, b) => a + b);
        let random = Math.random() * totalWeight;
        
        let weightSum = 0;
        for (let i = 0; i < weights.length; i++) {
            weightSum += weights[i];
            if (random <= weightSum) {
                return types[i];
            }
        }
        
        return types[0];
    }
    
    move() {
        if (this.isPaused || this.isGameOver) return;
        
        // Update direction
        this.direction = this.nextDirection;
        
        // Calculate new head position
        let head = {x: this.snake[0].x, y: this.snake[0].y};
        
        switch(this.direction) {
            case "up": head.y--; break;
            case "down": head.y++; break;
            case "left": head.x--; break;
            case "right": head.x++; break;
        }
        
        // Add new head
        this.snake.unshift(head);
        
        // Check food collision
        if (head.x === this.food.x && head.y === this.food.y) {
            this.eatFood();
        } else {
            // Remove tail if no food eaten
            this.snake.pop();
        }
        
        // Check collisions
        this.checkCollisions();
    }
    
    eatFood() {
        // Add score
        this.score += this.food.type.value;
        this.foodEaten++;
        
        // Update high score
        if (this.score > this.highScore) {
            this.highScore = this.score;
            localStorage.setItem("snakeHighScore", this.highScore);
        }
        
        // Speed up every 5 foods
        if (this.foodEaten % 5 === 0 && this.speed > 50) {
            this.speed -= 10;
        }
        
        // Create eating effect
        this.createEffect(this.food.x, this.food.y, this.food.type.color);
        
        // Generate new food
        this.generateFood();
    }
    
    checkCollisions() {
        let head = this.snake[0];
        
        // Wall collision
        if (head.x < 0 || head.x >= this.gridWidth || 
            head.y < 0 || head.y >= this.gridHeight) {
            this.gameOver("Wall crash!");
            return;
        }
        
        // Self collision
        for (let i = 1; i < this.snake.length; i++) {
            if (head.x === this.snake[i].x && head.y === this.snake[i].y) {
                this.gameOver("Self collision!");
                return;
            }
        }
    }
    
    gameOver(reason) {
        this.isGameOver = true;
        console.log(\`Game Over: \${reason}\`);
        
        // Create explosion effect
        let head = this.snake[0];
        this.createExplosion(head.x, head.y);
    }
    
    draw() {
        // Clear canvas
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        
        // Draw background
        this.drawBackground();
        
        // Draw grid (optional)
        if (this.showGrid) this.drawGrid();
        
        // Draw food
        this.drawFood();
        
        // Draw snake
        this.drawSnake();
        
        // Draw effects
        this.drawEffects();
        
        // Draw particles
        this.drawParticles();
        
        // Draw UI
        this.drawUI();
        
        // Draw game over screen
        if (this.isGameOver) {
            this.drawGameOver();
        }
    }
    
    drawSnake() {
        for (let i = 0; i < this.snake.length; i++) {
            let segment = this.snake[i];
            let x = segment.x * this.gridSize;
            let y = segment.y * this.gridSize;
            let size = this.gridSize;
            
            // Head is different
            if (i === 0) {
                this.drawHead(x, y, size);
            } else {
                this.drawBodySegment(x, y, size, i);
            }
        }
    }
    
    drawHead(x, y, size) {
        // Head color based on direction
        let headColor;
        switch(this.direction) {
            case "up": headColor = "#2ECC71"; break;
            case "down": headColor = "#27AE60"; break;
            case "left": headColor = "#229954"; break;
            case "right": headColor = "#1E8449"; break;
        }
        
        // Draw head with eyes
        this.ctx.fillStyle = headColor;
        this.ctx.fillRect(x, y, size, size);
        
        // Draw eyes
        this.ctx.fillStyle = "white";
        let eyeSize = size / 5;
        let eyeOffset = size / 3;
        
        // Eye positions based on direction
        if (this.direction === "right") {
            this.ctx.fillRect(x + size - eyeOffset, y + eyeOffset, eyeSize, eyeSize);
            this.ctx.fillRect(x + size - eyeOffset, y + size - eyeOffset - eyeSize, eyeSize, eyeSize);
        } else if (this.direction === "left") {
            this.ctx.fillRect(x + eyeOffset, y + eyeOffset, eyeSize, eyeSize);
            this.ctx.fillRect(x + eyeOffset, y + size - eyeOffset - eyeSize, eyeSize, eyeSize);
        } else if (this.direction === "up") {
            this.ctx.fillRect(x + eyeOffset, y + eyeOffset, eyeSize, eyeSize);
            this.ctx.fillRect(x + size - eyeOffset - eyeSize, y + eyeOffset, eyeSize, eyeSize);
        } else { // down
            this.ctx.fillRect(x + eyeOffset, y + size - eyeOffset, eyeSize, eyeSize);
            this.ctx.fillRect(x + size - eyeOffset - eyeSize, y + size - eyeOffset, eyeSize, eyeSize);
        }
    }
    
    drawBodySegment(x, y, size, index) {
        // Gradient based on position
        let greenValue = 200 - (index * 3);
        if (greenValue < 50) greenValue = 50;
        
        this.ctx.fillStyle = \`rgb(46, \${greenValue}, 113)\`;
        this.ctx.fillRect(x + 2, y + 2, size - 4, size - 4);
        
        // Inner highlight
        this.ctx.fillStyle = \`rgb(70, \${greenValue + 30}, 130)\`;
        this.ctx.fillRect(x + 4, y + 4, size - 8, size - 8);
    }
    
    drawFood() {
        let x = this.food.x * this.gridSize;
        let y = this.food.y * this.gridSize;
        let size = this.gridSize;
        let type = this.food.type;
        
        // Draw food with shadow
        this.ctx.shadowColor = "rgba(0,0,0,0.3)";
        this.ctx.shadowBlur = 5;
        this.ctx.shadowOffsetY = 2;
        
        this.ctx.fillStyle = type.color;
        this.ctx.beginPath();
        this.ctx.arc(
            x + size/2, 
            y + size/2, 
            size/2 - 2, 
            0, 
            Math.PI * 2
        );
        this.ctx.fill();
        
        // Reset shadow
        this.ctx.shadowColor = "transparent";
        
        // Draw shine effect
        this.ctx.fillStyle = "rgba(255,255,255,0.3)";
        this.ctx.beginPath();
        this.ctx.arc(
            x + size/3, 
            y + size/3, 
            size/6, 
            0, 
            Math.PI * 2
        );
        this.ctx.fill();
        
        // Draw value indicator
        this.ctx.fillStyle = "white";
        this.ctx.font = "bold 10px Arial";
        this.ctx.textAlign = "center";
        this.ctx.fillText(
            type.value, 
            x + size/2, 
            y + size/2 + 3
        );
    }
    
    drawUI() {
        // Draw score
        this.ctx.fillStyle = "#2C3E50";
        this.ctx.font = "bold 20px Arial";
        this.ctx.textAlign = "left";
        this.ctx.fillText(\`Score: \${this.score}\`, 10, 25);
        
        // Draw high score
        this.ctx.fillStyle = "#7F8C8D";
        this.ctx.font = "bold 16px Arial";
        this.ctx.fillText(\`High: \${this.highScore}\`, 10, 50);
        
        // Draw food counter
        this.ctx.fillText(\`Food: \${this.foodEaten}\`, 10, 75);
        
        // Draw speed indicator
        let speedPercent = ((200 - this.speed) / 150) * 100;
        this.ctx.fillText(\`Speed: \${Math.round(speedPercent)}%\`, 10, 100);
        
        // Draw controls help
        if (!this.isGameOver && this.foodEaten < 3) {
            this.ctx.fillStyle = "rgba(52, 73, 94, 0.8)";
            this.ctx.fillRect(10, 120, 200, 60);
            
            this.ctx.fillStyle = "white";
            this.ctx.font = "12px Arial";
            this.ctx.fillText("Controls:", 15, 140);
            this.ctx.fillText("Arrow Keys: Move", 15, 155);
            this.ctx.fillText("Space: Pause", 15, 170);
        }
    }
    
    drawGameOver() {
        // Semi-transparent overlay
        this.ctx.fillStyle = "rgba(0, 0, 0, 0.7)";
        this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
        
        // Game Over text
        this.ctx.fillStyle = "#E74C3C";
        this.ctx.font = "bold 48px Arial";
        this.ctx.textAlign = "center";
        this.ctx.fillText("GAME OVER", this.canvas.width/2, this.canvas.height/2 - 50);
        
        // Final score
        this.ctx.fillStyle = "#ECF0F1";
        this.ctx.font = "bold 24px Arial";
        this.ctx.fillText(\`Final Score: \${this.score}\`, this.canvas.width/2, this.canvas.height/2);
        
        // High score
        if (this.score === this.highScore) {
            this.ctx.fillStyle = "#F1C40F";
            this.ctx.fillText("NEW HIGH SCORE!", this.canvas.width/2, this.canvas.height/2 + 30);
        }
        
        // Restart instructions
        this.ctx.fillStyle = "#95A5A6";
        this.ctx.font = "18px Arial";
        this.ctx.fillText("Press R to restart", this.canvas.width/2, this.canvas.height/2 + 70);
    }
    
    createEffect(x, y, color) {
        this.effects.push({
            x: x * this.gridSize,
            y: y * this.gridSize,
            color: color,
            size: this.gridSize,
            life: 30
        });
    }
    
    createExplosion(x, y) {
        for (let i = 0; i < 20; i++) {
            this.particles.push({
                x: x * this.gridSize + this.gridSize/2,
                y: y * this.gridSize + this.gridSize/2,
                vx: (Math.random() - 0.5) * 10,
                vy: (Math.random() - 0.5) * 10,
                color: "#E74C3C",
                size: Math.random() * 5 + 2,
                life: 60
            });
        }
    }
    
    updateEffects() {
        // Update effects
        for (let i = this.effects.length - 1; i >= 0; i--) {
            let effect = this.effects[i];
            effect.life--;
            effect.size += 0.5;
            
            if (effect.life <= 0) {
                this.effects.splice(i, 1);
            }
        }
        
        // Update particles
        for (let i = this.particles.length - 1; i >= 0; i--) {
            let particle = this.particles[i];
            particle.x += particle.vx;
            particle.y += particle.vy;
            particle.vy += 0.1; // Gravity
            particle.life--;
            particle.size *= 0.97; // Shrink
            
            if (particle.life <= 0 || particle.size <= 0.5) {
                this.particles.splice(i, 1);
            }
        }
    }
    
    drawEffects() {
        // Draw eating effects
        for (let effect of this.effects) {
            this.ctx.globalAlpha = effect.life / 30;
            this.ctx.fillStyle = effect.color;
            this.ctx.beginPath();
            this.ctx.arc(
                effect.x + this.gridSize/2,
                effect.y + this.gridSize/2,
                effect.size/2,
                0,
                Math.PI * 2
            );
            this.ctx.fill();
        }
        this.ctx.globalAlpha = 1;
        
        // Draw particles
        for (let particle of this.particles) {
            this.ctx.globalAlpha = particle.life / 60;
            this.ctx.fillStyle = particle.color;
            this.ctx.beginPath();
            this.ctx.arc(
                particle.x,
                particle.y,
                particle.size,
                0,
                Math.PI * 2
            );
            this.ctx.fill();
        }
        this.ctx.globalAlpha = 1;
    }
    
    loop(timestamp) {
        // Calculate delta time
        if (!this.lastUpdate) this.lastUpdate = timestamp;
        let deltaTime = timestamp - this.lastUpdate;
        
        // Move snake at specified interval
        if (deltaTime > this.speed) {
            this.move();
            this.lastUpdate = timestamp;
        }
        
        // Update effects continuously
        this.updateEffects();
        
        // Always draw
        this.draw();
        
        // Continue loop
        requestAnimationFrame(this.gameLoop);
    }
    
    handleKey(event) {
        if (this.isGameOver && event.key.toLowerCase() === "r") {
            this.reset();
            return;
        }
        
        if (event.key === " ") {
            this.isPaused = !this.isPaused;
            return;
        }
        
        if (this.isPaused || this.isGameOver) return;
        
        switch(event.key) {
            case "ArrowUp":
                if (this.direction !== "down") 
                    this.nextDirection = "up";
                break;
            case "ArrowDown":
                if (this.direction !== "up") 
                    this.nextDirection = "down";
                break;
            case "ArrowLeft":
                if (this.direction !== "right") 
                    this.nextDirection = "left";
                break;
            case "ArrowRight":
                if (this.direction !== "left") 
                    this.nextDirection = "right";
                break;
        }
    }
    
    togglePause() {
        this.isPaused = !this.isPaused;
    }
}

