                let speedPercent = ((200 - game.speed) / 150) * 100;
                document.getElementById('speedDisplay').textContent = Math.round(speedPercent) + '%';
                
                // Update pause button text
                document.getElementById('pauseBtn').textContent = 
                    game.isPaused ? 'â–¶ï¸ Resume' : 'â¸ï¸ Pause';
            }, 100);
        });
        
        // Utility functions
        function toggleGrid() {
            game.showGrid = !game.showGrid;
            document.getElementById('gridBtn').textContent = 
                game.showGrid ? 'ğŸ“ Hide Grid' : 'ğŸ“ Show Grid';
        }
        
        function toggleSound() {
            // Sound implementation would go here
            let soundOn = document.getElementById('soundBtn').textContent.includes('On');
            document.getElementById('soundBtn').textContent = 
                soundOn ? 'ğŸ”‡ Sound: Off' : 'ğŸ”Š Sound: On';
        }
        
        function setDifficulty(level) {
            // Remove active class from all buttons
            document.querySelectorAll('.difficulty-btn').forEach(btn => {
                btn.classList.remove('active');
            });
            
            // Add active class to clicked button
            event.target.classList.add('active');
            
            // Set difficulty
            switch(level) {
                case 'easy':
                    game.speed = 200;
                    break;
                case 'medium':
                    game.speed = 150;
                    break;
                case 'hard':
                    game.speed = 100;
                    break;
                case 'insane':
                    game.speed = 50;
                    break;
            }
        }
        
        // Mobile controls
        function moveUp() { game.nextDirection = 'up'; }
        function moveDown() { game.nextDirection = 'down'; }
        function moveLeft() { game.nextDirection = 'left'; }
        function moveRight() { game.nextDirection = 'right'; }
        function togglePauseMobile() { game.togglePause(); }
        
        // Touch controls for mobile
        let touchStartX = 0;
        let touchStartY = 0;
        
        document.addEventListener('touchstart', (e) => {
            touchStartX = e.changedTouches[0].screenX;
            touchStartY = e.changedTouches[0].screenY;
        });
        
        document.addEventListener('touchend', (e) => {
            let touchEndX = e.changedTouches[0].screenX;
            let touchEndY = e.changedTouches[0].screenY;
            
            let dx = touchEndX - touchStartX;
            let dy = touchEndY - touchStartY;
            
            // Determine swipe direction
            if (Math.abs(dx) > Math.abs(dy)) {
                // Horizontal swipe
                if (dx > 0 && game.direction !== 'left') {
                    game.nextDirection = 'right';
                } else if (dx < 0 && game.direction !== 'right') {
                    game.nextDirection = 'left';
                }
            } else {
                // Vertical swipe
                if (dy > 0 && game.direction !== 'up') {
                    game.nextDirection = 'down';
                } else if (dy < 0 && game.direction !== 'down') {
                    game.nextDirection = 'up';
                }
            }
        });
