class PhysicsObject {
    constructor(x, y) {
        // Position
        this.x = x;
        this.y = y;
        
        // Velocity (pixels per second)
        this.vx = 0;
        this.vy = 0;
        
        // Acceleration (pixels per secondÂ²)
        this.ax = 0;
        this.ay = 0;
        
        // Physical properties
        this.mass = 1;
        this.width = 50;
        this.height = 50;
        this.color = "#FF6B6B";
        
        // For rotation
        this.rotation = 0;
        this.angularVelocity = 0;
    }
    
    update(deltaTime) {
        // Convert deltaTime from milliseconds to seconds
        let dt = deltaTime / 1000;
        
        // Update velocity with acceleration
        this.vx += this.ax * dt;
        this.vy += this.ay * dt;
        
        // Update position with velocity
        this.x += this.vx * dt;
        this.y += this.vy * dt;
        
        // Update rotation
        this.rotation += this.angularVelocity * dt;
        
        // Keep rotation between 0 and 360 degrees
        if (this.rotation >= 360) this.rotation -= 360;
        if (this.rotation < 0) this.rotation += 360;
    }
    
    applyForce(fx, fy) {
        // F = ma, so a = F/m
        this.ax += fx / this.mass;
        this.ay += fy / this.mass;
    }
    
    applyImpulse(ix, iy) {
        // Impulse directly changes velocity
        this.vx += ix / this.mass;
        this.vy += iy / this.mass;
    }
    
    draw(ctx) {
        ctx.save();
        
        // Move to object position
        ctx.translate(this.x, this.y);
        
        // Apply rotation
        ctx.rotate(this.rotation * Math.PI / 180);
        
        // Draw object
        ctx.fillStyle = this.color;
        ctx.fillRect(
            -this.width / 2,
            -this.height / 2,
            this.width,
            this.height
        );
        
        // Draw velocity vector
        this.drawVelocityVector(ctx);
        
        ctx.restore();
    }
    
    drawVelocityVector(ctx) {
        // Draw line showing velocity direction and magnitude
        let vectorScale = 0.1; // Scale for visualization
        let vx = this.vx * vectorScale;
        let vy = this.vy * vectorScale;
        
        // Only draw if moving
        if (Math.abs(vx) > 1 || Math.abs(vy) > 1) {
            ctx.strokeStyle = "#2196F3";
            ctx.lineWidth = 2;
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(vx, vy);
            ctx.stroke();
            
            // Draw arrowhead
            let angle = Math.atan2(vy, vx);
            let arrowSize = 10;
            
            ctx.beginPath();
            ctx.moveTo(vx, vy);
            ctx.lineTo(
                vx - arrowSize * Math.cos(angle - Math.PI / 6),
                vy - arrowSize * Math.sin(angle - Math.PI / 6)
            );
            ctx.lineTo(
                vx - arrowSize * Math.cos(angle + Math.PI / 6),
                vy - arrowSize * Math.sin(angle + Math.PI / 6)
            );
            ctx.closePath();
            ctx.fillStyle = "#2196F3";
            ctx.fill();
        }
    }
}

