class PhysicsDemo {
    constructor(canvasId) {
        this.canvas = document.getElementById(canvasId);
        this.ctx = this.canvas.getContext("2d");
        this.objects = [];
        this.lastTime = 0;
        this.isRunning = true;
        
        // Create demo objects
        this.createDemoObjects();
        
        // Set up mouse interaction
        this.setupMouse();
        
        // Start animation
        this.animate();
    }
    
    createDemoObjects() {
        // Different types of physics objects
        this.objects = [
            // Rocket with constant thrust
            {
                x: 100, y: 100,
                vx: 0, vy: 0,
                ax: 50, ay: 0, // Constant acceleration right
                mass: 2,
                color: "#FF6B6B",
                radius: 20,
                type: "rocket"
            },
            
            // Ball with initial velocity
            {
                x: 300, y: 200,
                vx: 100, vy: -50, // Moving up-right
                ax: 0, ay: 0,
                mass: 1,
                color: "#4ECDC4",
                radius: 25,
                type: "ball"
            },
            
            // Heavy object (slow to accelerate)
            {
                x: 500, y: 150,
                vx: 0, vy: 0,
                ax: 0, ay: 0,
                mass: 5, // Heavy!
                color: "#FFD166",
                radius: 30,
                type: "heavy"
            },
            
            // Fast, light object
            {
                x: 200, y: 300,
                vx: 0, vy: 0,
                ax: 0, ay: 0,
                mass: 0.5, // Light!
                color: "#06D6A0",
                radius: 15,
                type: "light"
            }
        ];
    }
    
    setupMouse() {
        this.canvas.addEventListener("mousedown", (e) => {
            let rect = this.canvas.getBoundingClientRect();
            let mouseX = e.clientX - rect.left;
            let mouseY = e.clientY - rect.top;
            
            // Launch object from click
            this.launchFromPoint(mouseX, mouseY);
        });
        
        this.canvas.addEventListener("mousemove", (e) => {
            if (e.buttons === 1) { // Left mouse button held
                let rect = this.canvas.getBoundingClientRect();
                let mouseX = e.clientX - rect.left;
                let mouseY = e.clientY - rect.top;
                
                // Apply force towards mouse
                this.applyForceTowards(mouseX, mouseY);
            }
        });
        
        // Keyboard controls
        document.addEventListener("keydown", (e) => {
            switch(e.key) {
                case " ":
                    this.isRunning = !this.isRunning;
                    break;
                case "r":
                    this.createDemoObjects();
                    break;
                case "g":
                    this.toggleGravity();
                    break;
            }
        });
    }
    
    launchFromPoint(x, y) {
        // Create new object at click position
        let obj = {
            x: x,
            y: y,
            vx: (Math.random() - 0.5) * 400, // Random velocity
            vy: (Math.random() - 0.5) * 400,
            ax: 0,
            ay: 0,
            mass: Math.random() * 3 + 0.5,
            color: this.getRandomColor(),
            radius: Math.random() * 20 + 10,
            type: "custom"
        };
        
        this.objects.push(obj);
        
        // Limit number of objects
        if (this.objects.length > 20) {
            this.objects.shift();
        }
    }
    
    applyForceTowards(targetX, targetY) {
        // Apply force to all objects towards mouse
        for (let obj of this.objects) {
            // Calculate direction to mouse
            let dx = targetX - obj.x;
            let dy = targetY - obj.y;
            let distance = Math.sqrt(dx * dx + dy * dy);
            
            if (distance > 10) { // Don't apply if very close
                // Normalize direction
                dx /= distance;
                dy /= distance;
                
                // Apply force (stronger when closer)
                let force = 5000 / (distance * distance);
                obj.ax += dx * force / obj.mass;
                obj.ay += dy * force / obj.mass;
            }
        }
    }
    
    toggleGravity() {
        this.gravityEnabled = !this.gravityEnabled;
    }
    
    update(deltaTime) {
        if (!this.isRunning) return;
        
        let dt = deltaTime / 1000; // Convert to seconds
        
        for (let obj of this.objects) {
            // Apply gravity if enabled
            if (this.gravityEnabled) {
                obj.ay += 500; // Gravity acceleration
            }
            
            // Apply air resistance (damping)
            obj.vx *= 0.99;
            obj.vy *= 0.99;
            
            // Update velocity
            obj.vx += obj.ax * dt;
            obj.vy += obj.ay * dt;
            
            // Update position
            obj.x += obj.vx * dt;
            obj.y += obj.vy * dt;
            
            // Bounce off walls
            if (obj.x - obj.radius < 0) {
                obj.x = obj.radius;
                obj.vx = Math.abs(obj.vx) * 0.8; // Bounce with energy loss
            }
            if (obj.x + obj.radius > this.canvas.width) {
                obj.x = this.canvas.width - obj.radius;
                obj.vx = -Math.abs(obj.vx) * 0.8;
            }
            if (obj.y - obj.radius < 0) {
                obj.y = obj.radius;
                obj.vy = Math.abs(obj.vy) * 0.8;
            }
            if (obj.y + obj.radius > this.canvas.height) {
                obj.y = this.canvas.height - obj.radius;
                obj.vy = -Math.abs(obj.vy) * 0.8;
            }
            
            // Reset acceleration for next frame
            obj.ax = 0;
            obj.ay = 0;
        }
    }
    
    draw() {
        // Clear canvas
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        
        // Draw grid background
        this.drawGrid();
        
        // Draw all objects
        for (let obj of this.objects) {
            this.drawObject(obj);
            
            // Draw velocity vectors
            this.drawVelocityVector(obj);
            
            // Draw acceleration vectors
            this.drawAccelerationVector(obj);
        }
        
        // Draw UI
        this.drawUI();
    }
    
    drawObject(obj) {
        this.ctx.save();
        
        // Draw object
        this.ctx.fillStyle = obj.color;
        this.ctx.beginPath();
        this.ctx.arc(obj.x, obj.y, obj.radius, 0, Math.PI * 2);
        this.ctx.fill();
        
        // Draw outline
        this.ctx.strokeStyle = "rgba(0, 0, 0, 0.5)";
        this.ctx.lineWidth = 2;
        this.ctx.stroke();
        
        // Draw mass indicator
        this.ctx.fillStyle = "white";
        this.ctx.font = "bold 12px Arial";
        this.ctx.textAlign = "center";
        this.ctx.fillText(
            \`m:\${obj.mass.toFixed(1)}\`,
            obj.x,
            obj.y + 4
        );
        
        this.ctx.restore();
    }
    
    drawVelocityVector(obj) {
        let scale = 0.05; // Scale for visualization
        let vx = obj.vx * scale;
        let vy = obj.vy * scale;
        
        if (Math.abs(vx) > 1 || Math.abs(vy) > 1) {
            this.ctx.strokeStyle = "#2196F3";
            this.ctx.lineWidth = 2;
            this.ctx.beginPath();
            this.ctx.moveTo(obj.x, obj.y);
            this.ctx.lineTo(obj.x + vx, obj.y + vy);
            this.ctx.stroke();
            
            // Label
            this.ctx.fillStyle = "#2196F3";
            this.ctx.font = "10px Arial";
            this.ctx.fillText(
                \`v:\${Math.sqrt(obj.vx*obj.vx + obj.vy*obj.vy).toFixed(0)}\`,
                obj.x + vx + 10,
                obj.y + vy
            );
        }
    }
    
    drawAccelerationVector(obj) {
        // Show any current acceleration
        if (Math.abs(obj.ax) > 0.1 || Math.abs(obj.ay) > 0.1) {
            let scale = 0.01; // Different scale for acceleration
            let ax = obj.ax * scale;
            let ay = obj.ay * scale;
            
            this.ctx.strokeStyle = "#FF9800";
            this.ctx.lineWidth = 2;
            this.ctx.beginPath();
            this.ctx.moveTo(obj.x, obj.y);
            this.ctx.lineTo(obj.x + ax, obj.y + ay);
            this.ctx.stroke();
        }
    }
    
    drawGrid() {
        this.ctx.strokeStyle = "rgba(0, 0, 0, 0.1)";
        this.ctx.lineWidth = 1;
        
        // Vertical lines
        for (let x = 0; x <= this.canvas.width; x += 50) {
            this.ctx.beginPath();
            this.ctx.moveTo(x, 0);
            this.ctx.lineTo(x, this.canvas.height);
            this.ctx.stroke();
        }
        
        // Horizontal lines
        for (let y = 0; y <= this.canvas.height; y += 50) {
            this.ctx.beginPath();
            this.ctx.moveTo(0, y);
            this.ctx.lineTo(this.canvas.width, y);
            this.ctx.stroke();
        }
    }
    
    drawUI() {
        this.ctx.fillStyle = "#2C3E50";
        this.ctx.font = "14px Arial";
        this.ctx.textAlign = "left";
        
        this.ctx.fillText(\`Objects: \${this.objects.length}\`, 10, 20);
