class Rocket {
    constructor() {
        // Position & Velocity
        this.x = 0;
        this.y = EARTH_RADIUS; // Start at surface
        this.vx = 0;
        this.vy = 0;
        
        // Mass properties
        this.dryMass = 50000;  // kg (empty rocket)
        this.fuelMass = 400000; // kg (fuel)
        this.payloadMass = 10000; // kg (satellite)
        
        // Engine properties
        this.thrust = 7600000; // Newtons (Saturn V)
        this.burnRate = 13000; // kg/s
        this.isp = 263; // Specific impulse (seconds)
        
        // Flight state
        this.isLaunching = false;
        this.stage = 1;
        this.angle = 90; // Degrees (90 = straight up)
        
        // Physics calculations
        this.gravity = 9.81; // m/sÂ²
        this.atmosphere = true;
    }
    
    get totalMass() {
        return this.dryMass + this.fuelMass + this.payloadMass;
    }
    
    update(dt) {
        if (!this.isLaunching) return;
        
        // Calculate current mass
        let currentMass = this.totalMass;
        
        // Calculate acceleration from thrust
        if (this.fuelMass > 0) {
            let thrustAcceleration = this.thrust / currentMass;
            
            // Convert angle to radians
            let angleRad = this.angle * Math.PI / 180;
            
            // Apply thrust acceleration
            this.vx += Math.cos(angleRad) * thrustAcceleration * dt;
            this.vy += Math.sin(angleRad) * thrustAcceleration * dt;
            
            // Consume fuel
            this.fuelMass -= this.burnRate * dt;
            if (this.fuelMass < 0) this.fuelMass = 0;
        }
        
        // Calculate gravity (weaker with altitude)
        let altitude = this.getAltitude();
        let gravityStrength = this.calculateGravity(altitude);
        
        // Apply gravity (always towards Earth center)
        let angleToCenter = Math.atan2(this.y, this.x);
        this.vx += Math.cos(angleToCenter + Math.PI) * gravityStrength * dt;
        this.vy += Math.sin(angleToCenter + Math.PI) * gravityStrength * dt;
        
        // Apply atmospheric drag
        if (this.atmosphere && altitude < 100000) {
            let drag = this.calculateDrag(altitude);
            let speed = Math.sqrt(this.vx*this.vx + this.vy*this.vy);
            
            if (speed > 0) {
                this.vx -= (this.vx / speed) * drag * dt;
                this.vy -= (this.vy / speed) * drag * dt;
            }
        }
        
        // Update position
        this.x += this.vx * dt;
        this.y += this.vy * dt;
        
        // Check staging
        this.checkStaging();
    }
}
