// Earth constants
const EARTH_RADIUS = 6371000; // meters
const EARTH_MASS = 5.972e24; // kg
const GRAVITY_CONSTANT = 6.67430e-11;

class PlanetaryPhysics {
    // Calculate gravity at altitude
    static calculateGravity(altitude) {
        let distance = EARTH_RADIUS + altitude;
        return (GRAVITY_CONSTANT * EARTH_MASS) / (distance * distance);
    }
    
    // Calculate orbital velocity at altitude
    static orbitalVelocity(altitude) {
        let distance = EARTH_RADIUS + altitude;
        return Math.sqrt((GRAVITY_CONSTANT * EARTH_MASS) / distance);
    }
    
    // Check if in orbit
    static isInOrbit(rocket) {
        let altitude = rocket.getAltitude();
        if (altitude < 160000) return false; // Too low
        
        let velocity = Math.sqrt(rocket.vx*rocket.vx + rocket.vy*rocket.vy);
        let orbitalVel = this.orbitalVelocity(altitude);
        
        // Within 10% of orbital velocity
        return Math.abs(velocity - orbitalVel) / orbitalVel < 0.1;
    }
    
    // Calculate atmospheric density (simplified)
    static atmosphericDensity(altitude) {
        if (altitude > 100000) return 0; // Space
        
        // Exponential decay model
        let scaleHeight = 8500; // meters
        let seaLevelDensity = 1.225; // kg/mÂ³
        
        return seaLevelDensity * Math.exp(-altitude / scaleHeight);
    }
    
    // Calculate drag force
    static calculateDrag(rocket, altitude) {
        let density = this.atmosphericDensity(altitude);
        let velocity = Math.sqrt(rocket.vx*rocket.vx + rocket.vy*rocket.vy);
        let crossSection = rocket.getCrossSection();
        let dragCoefficient = 0.3; // Rough value for rockets
        
        return 0.5 * density * velocity * velocity * 
               crossSection * dragCoefficient;
    }
}

// Rocket helper methods
Rocket.prototype.getAltitude = function() {
    let distance = Math.sqrt(this.x*this.x + this.y*this.y);
    return distance - EARTH_RADIUS;
};

Rocket.prototype.getCrossSection = function() {
    // Base cross-section area (Ï€rÂ²)
    let baseRadius = 5; // meters
    return Math.PI * baseRadius * baseRadius;
};

Rocket.prototype.calculateGravity = function(altitude) {
    return PlanetaryPhysics.calculateGravity(altitude);
};

Rocket.prototype.calculateDrag = function(altitude) {
    return PlanetaryPhysics.calculateDrag(this, altitude);
};
