// Earth's gravity constant
const GRAVITY = 9.81; // meters per secondÂ²

// Simple gravity implementation
class GravitySimulator {
    constructor() {
        this.objects = [];
        this.gravityStrength = GRAVITY;
        this.gravityDirection = { x: 0, y: 1 }; // Down
    }
    
    addObject(obj) {
        this.objects.push(obj);
    }
    
    update(deltaTime) {
        let dt = deltaTime / 1000; // Convert to seconds
        
        for (let obj of this.objects) {
            // Apply gravity force
            let gravityForce = {
                x: this.gravityDirection.x * this.gravityStrength * obj.mass,
                y: this.gravityDirection.y * this.gravityStrength * obj.mass
            };
            
            // Convert force to acceleration (F = ma, so a = F/m)
            obj.ax += gravityForce.x / obj.mass;
            obj.ay += gravityForce.y / obj.mass;
            
            // Update velocity with acceleration
            obj.vx += obj.ax * dt;
            obj.vy += obj.ay * dt;
            
            // Update position with velocity
            obj.x += obj.vx * dt;
            obj.y += obj.vy * dt;
            
            // Reset acceleration for next frame
            obj.ax = 0;
            obj.ay = 0;
            
            // Apply ground collision
            this.handleGroundCollision(obj);
        }
    }
    
    handleGroundCollision(obj) {
        if (obj.y + obj.radius > GROUND_LEVEL) {
            // Hit the ground
            obj.y = GROUND_LEVEL - obj.radius;
            
            // Apply bounce with energy loss
            obj.vy = -obj.vy * obj.bounciness;
            
            // Apply friction when on ground
            if (Math.abs(obj.vy) < 5) { // Almost stopped bouncing
                obj.vy = 0;
                this.applyFriction(obj);
            }
        }
    }
    
    applyFriction(obj) {
        if (obj.onGround) {
            // Kinetic friction opposes motion
            let frictionForce = obj.mass * this.gravityStrength * obj.frictionCoefficient;
            
            if (obj.vx > 0) {
                obj.vx = Math.max(0, obj.vx - frictionForce);
            } else if (obj.vx < 0) {
                obj.vx = Math.min(0, obj.vx + frictionForce);
            }
        }
    }
}

