            <h3>â„ï¸ Friction Types & Formulas</h3>
            <pre style="background: white; padding: 15px; border-radius: 5px;">
class FrictionPhysics {
    constructor() {
        // Surface friction coefficients
        this.surfaces = {
            ice: 0.02,
            wetIce: 0.05,
            snow: 0.1,
            wetRoad: 0.2,
            dryRoad: 0.6,
            grass: 0.4,
            mud: 0.8,
            sand: 0.5,
            rubber: 0.9
        };
        
        // Air resistance coefficient
        this.airDensity = 1.225; // kg/mÂ³ at sea level
    }
    
    // Static friction (object at rest)
    staticFriction(normalForce, frictionCoefficient) {
        return normalForce * frictionCoefficient;
    }
    
    // Kinetic friction (object moving)
    kineticFriction(normalForce, frictionCoefficient) {
        return normalForce * frictionCoefficient * 0.8; // Usually less than static
    }
    
    // Air resistance (drag)
    airResistance(velocity, crossSectionArea, dragCoefficient) {
        let speed = Math.sqrt(velocity.x * velocity.x + 
                             velocity.y * velocity.y);
        
        if (speed === 0) return { x: 0, y: 0 };
        
        // Drag force = 0.5 * density * velocityÂ² * area * Cd
        let dragMagnitude = 0.5 * this.airDensity * 
                           speed * speed * 
                           crossSectionArea * 
                           dragCoefficient;
        
        // Opposite direction of motion
        return {
            x: -(velocity.x / speed) * dragMagnitude,
            y: -(velocity.y / speed) * dragMagnitude
        };
    }
    
    // Rolling resistance (for wheels)
    rollingResistance(normalForce, coefficient) {
        return normalForce * coefficient;
    }
    
    // Apply friction to object
    applyFriction(obj, surfaceType) {
        let frictionCoefficient = this.surfaces[surfaceType] || 0.5;
        let normalForce = obj.mass * 9.81; // Weight
        
        // Check if object is moving
        let speed = Math.sqrt(obj.vx * obj.vx + obj.vy * obj.vy);
        
        if (speed === 0) {
            // Static friction - needs force to start moving
            let maxStaticFriction = this.staticFriction(normalForce, 
                                                       frictionCoefficient);
            // If applied force is less than static friction, don't move
            let appliedForce = Math.sqrt(obj.ax * obj.ax + obj.ay * obj.ay);
            if (appliedForce * obj.mass < maxStaticFriction) {
                obj.vx = 0;
                obj.vy = 0;
                return;
            }
        } else {
            // Kinetic friction - opposes motion
            let frictionForce = this.kineticFriction(normalForce, 
                                                   frictionCoefficient);
            
            // Calculate friction acceleration (opposite to velocity)
            let frictionAcceleration = frictionForce / obj.mass;
            
            // Reduce velocity by friction
            if (speed > frictionAcceleration) {
                obj.vx -= (obj.vx / speed) * frictionAcceleration;
                obj.vy -= (obj.vy / speed) * frictionAcceleration;
            } else {
                // Stop if friction is greater than motion
                obj.vx = 0;
                obj.vy = 0;
            }
        }
    }
    
    // Apply air resistance
    applyAirResistance(obj, dragCoefficient) {
        let crossSection = obj.radius * obj.radius * Math.PI;
        let dragForce = this.airResistance(
            { x: obj.vx, y: obj.vy },
            crossSection,
            dragCoefficient
        );
        
        // Apply drag as acceleration
        obj.ax += dragForce.x / obj.mass;
        obj.ay += dragForce.y / obj.mass;
    }
}

// Example: Car on different surfaces
class Car {
    constructor() {
        this.x = 100;
        this.y = 300;
        this.vx = 0;
        this.vy = 0;
        this.ax = 0;
        this.ay = 0;
        this.mass = 1000; // kg
        this.engineForce = 2000; // N
        this.brakeForce = 5000; // N
        this.tireFriction = 0.7;
        this.dragCoefficient = 0.3;
        this.frontalArea = 2; // mÂ²
        this.wheelRadius = 0.3; // meters
        
        this.engineOn = false;
        this.braking = false;
    }
    
    update(dt, surfaceType) {
        let friction = new FrictionPhysics();
        
        // Engine force
        if (this.engineOn) {
            this.ax += this.engineForce / this.mass;
        }
        
        // Braking force
        if (this.braking) {
            let brakeAcceleration = this.brakeForce / this.mass;
            if (this.vx > 0) {
                this.ax -= Math.min(brakeAcceleration, this.vx / dt);
            } else if (this.vx < 0) {
                this.ax += Math.min(brakeAcceleration, -this.vx / dt);
            }
        }
        
        // Update velocity
        this.vx += this.ax * dt;
        this.vy += this.ay * dt;
        
        // Apply surface friction
        friction.applyFriction(this, surfaceType);
        
        // Apply air resistance
        let airDrag = friction.airResistance(
            { x: this.vx, y: 0 },
            this.frontalArea,
            this.dragCoefficient
        );
        this.vx += airDrag.x / this.mass * dt;
        
        // Apply rolling resistance
        let normalForce = this.mass * 9.81;
        let rollingResistance = friction.rollingResistance(
            normalForce,
            0.01 // Typical for car tires
        );
        let rollingAcceleration = rollingResistance / this.mass;
        
        if (this.vx > 0) {
            this.vx -= rollingAcceleration * dt;
            if (this.vx < 0) this.vx = 0;
        } else if (this.vx < 0) {
            this.vx += rollingAcceleration * dt;
            if (this.vx > 0) this.vx = 0;
        }
        
        // Update position
        this.x += this.vx * dt;
        
        // Reset acceleration
        this.ax = 0;
        this.ay = 0;
    }
