class MoonLander {
    constructor() {
        // Position and velocity
        this.x = 400;
        this.y = 50;
        this.vx = 0;
        this.vy = 0;
        
        // Moon physics
        this.moonGravity = 1.62; // m/sÂ²
        this.groundLevel = 550;
        
        // Spacecraft properties
        this.fuel = 1000; // liters
        this.fuelConsumption = 10; // liters per second at full thrust
        this.maxThrust = 20; // m/sÂ²
        this.mass = 5000; // kg (dry mass)
        this.fuelMass = 2000; // kg
        
        // Controls
        this.thrust = 0; // 0 to 1
        this.rotation = 0; // -45 to 45 degrees
        
        // Game state
        this.landed = false;
        this.crashed = false;
        this.score = 0;
        this.landingZone = this.generateLandingZone();
        
        // Physics calculations
        this.totalMass = this.mass + this.fuelMass;
    }
    
    update(deltaTime) {
        if (this.landed || this.crashed) return;
        
        let dt = deltaTime / 1000; // Convert to seconds
        
        // Calculate current mass (fuel affects mass!)
        this.totalMass = this.mass + this.fuelMass;
        
        // Apply gravity
        this.vy += this.moonGravity * dt;
        
        // Apply thrust if fuel available
        if (this.fuel > 0 && this.thrust > 0) {
            // Calculate thrust force
            let thrustForce = this.thrust * this.maxThrust * this.totalMass;
            
            // Convert rotation to radians
            let angleRad = this.rotation * Math.PI / 180;
            
            // Apply thrust in correct direction
            this.vx += Math.sin(angleRad) * thrustForce / this.totalMass * dt;
            this.vy -= Math.cos(angleRad) * thrustForce / this.totalMass * dt;
            
            // Consume fuel
            let fuelUsed = this.fuelConsumption * this.thrust * dt;
            this.fuel = Math.max(0, this.fuel - fuelUsed);
            this.fuelMass = this.fuel * 2; // 1 liter â‰ˆ 2 kg for rocket fuel
            
            // Add particle effects
            this.createExhaustParticles(angleRad);
        }
        
        // Update position
        this.x += this.vx * dt;
        this.y += this.vy * dt;
        
        // Check for landing/crash
        this.checkLanding();
        
        // Update particles
        this.updateParticles(dt);
    }
    
    checkLanding() {
        if (this.y >= this.groundLevel) {
            // Hit the ground
            this.y = this.groundLevel;
            
            // Check if over landing zone
            let inZone = Math.abs(this.x - this.landingZone.x) < 
                        this.landingZone.width / 2;
            
            // Check landing speed
            let verticalSpeed = Math.abs(this.vy);
            let horizontalSpeed = Math.abs(this.vx);
            
            if (verticalSpeed < 3 && horizontalSpeed < 2) {
                // Successful landing!
                this.landed = true;
                this.calculateScore(inZone, verticalSpeed, horizontalSpeed);
            } else {
                // Crash!
                this.crashed = true;
                this.createExplosion();
            }
        }
    }
    
    calculateScore(inZone, verticalSpeed, horizontalSpeed) {
        // Base score
        this.score = 1000;
        
        // Landing zone bonus
        if (inZone) {
            this.score += 500;
        }
        
        // Soft landing bonus
        let softLandingBonus = (3 - verticalSpeed) * 200;
        this.score += softLandingBonus;
        
        // Horizontal accuracy bonus
        let horizontalBonus = (2 - horizontalSpeed) * 100;
        this.score += horizontalBonus;
        
        // Fuel bonus
        let fuelBonus = this.fuel * 2;
        this.score += fuelBonus;
        
        // Perfect landing achievement
        if (inZone && verticalSpeed < 1 && horizontalSpeed < 0.5) {
            this.score += 1000;
        }
    }
    
    generateLandingZone() {
        return {
            x: 300 + Math.random() * 200,
            width: 60 + Math.random() * 40,
            color: "#7CFC00"
        };
    }
    
    createExhaustParticles(angle) {
        if (this.thrust > 0) {
            // Create particles from engine
            for (let i = 0; i < 5; i++) {
                this.particles.push({
                    x: this.x - Math.sin(angle) * 20,
                    y: this.y + Math.cos(angle) * 20,
                    vx: -Math.sin(angle) * 100 + (Math.random() - 0.5) * 50,
                    vy: Math.cos(angle) * 100 + (Math.random() - 0.5) * 50,
                    life: 1.0,
                    size: 3 + Math.random() * 4,
                    color: this.getExhaustColor()
                });
            }
        }
    }
}
