// Projectile motion formulas
class ProjectilePhysics {
    constructor() {
        this.gravity = 9.81; // m/sÂ²
    }
    
    // Calculate launch velocity components
    calculateLaunchComponents(speed, angle) {
        // Convert angle to radians
        let angleRad = angle * Math.PI / 180;
        
        // Calculate components
        return {
            vx: speed * Math.cos(angleRad), // Horizontal velocity
            vy: speed * Math.sin(angleRad)  // Vertical velocity
        };
    }
    
    // Calculate maximum height
    calculateMaxHeight(initialVy) {
        // vÂ² = uÂ² + 2as â†’ 0 = vyÂ² - 2gh â†’ h = vyÂ²/(2g)
        return (initialVy * initialVy) / (2 * this.gravity);
    }
    
    // Calculate time of flight
    calculateFlightTime(initialVy, launchHeight = 0) {
        // Solve quadratic: 0 = h + vy*t - 0.5*g*tÂ²
        // For ground launch: t = 2*vy/g
        if (launchHeight === 0) {
            return (2 * initialVy) / this.gravity;
        }
        
        // For elevated launch
        let a = -0.5 * this.gravity;
        let b = initialVy;
        let c = launchHeight;
        
        // Quadratic formula
        let discriminant = b * b - 4 * a * c;
        if (discriminant < 0) return 0;
        
        let t1 = (-b + Math.sqrt(discriminant)) / (2 * a);
        let t2 = (-b - Math.sqrt(discriminant)) / (2 * a);
        
        // Return positive time
        return Math.max(t1, t2);
    }
    
    // Calculate range (horizontal distance)
    calculateRange(vx, flightTime) {
        return vx * flightTime;
    }
    
    // Calculate optimal angle for maximum range (45Â° for no air resistance)
    calculateOptimalAngle() {
        return 45; // Degrees
    }
    
    // Calculate position at time t
    calculatePosition(initialX, initialY, vx, vy, time) {
        return {
            x: initialX + vx * time,
            y: initialY + vy * time - 0.5 * this.gravity * time * time
        };
    }
    
    // Calculate trajectory points
    calculateTrajectory(initialX, initialY, speed, angle, points = 50) {
        let components = this.calculateLaunchComponents(speed, angle);
        let flightTime = this.calculateFlightTime(components.vy, initialY);
        
        let trajectory = [];
        
        for (let i = 0; i <= points; i++) {
            let t = (i / points) * flightTime;
            let pos = this.calculatePosition(initialX, initialY, 
                                           components.vx, components.vy, t);
            trajectory.push(pos);
        }
        
        return trajectory;
    }
}

// Example: Cannon projectile
class CannonProjectile {
    constructor(x, y, speed, angle) {
        this.x = x;
        this.y = y;
        this.speed = speed;
        this.angle = angle; // Degrees
        
        this.physics = new ProjectilePhysics();
        
        // Calculate initial velocity components
        let components = this.physics.calculateLaunchComponents(speed, angle);
        this.vx = components.vx;
        this.vy = components.vy;
        
        // Projectile properties
        this.radius = 10;
        this.color = "#FF6B6B";
        this.trail = [];
        this.maxTrailLength = 20;
        
        // Time tracking
        this.time = 0;
        this.isActive = true;
        
        // Calculate trajectory for prediction
        this.predictedTrajectory = this.physics.calculateTrajectory(
            x, y, speed, angle, 30
        );
    }
    
    update(deltaTime) {
        if (!this.isActive) return;
        
        let dt = deltaTime / 1000; // Convert to seconds
        this.time += dt;
        
        // Update position using projectile motion equations
        this.x += this.vx * dt;
        this.vy -= this.physics.gravity * dt; // Gravity reduces vertical velocity
        this.y += this.vy * dt;
        
        // Add to trail
        this.trail.push({x: this.x, y: this.y});
        if (this.trail.length > this.maxTrailLength) {
            this.trail.shift();
        }
        
        // Check if hit ground
        if (this.y <= 0) {
            this.y = 0;
            this.isActive = false;
            this.createImpactEffect();
        }
    }
    
    draw(ctx) {
        // Draw trail
        if (this.trail.length > 1) {
            ctx.strokeStyle = "rgba(255, 107, 107, 0.5)";
            ctx.lineWidth = 2;
            ctx.beginPath();
            ctx.moveTo(this.trail[0].x, this.trail[0].y);
            
            for (let i = 1; i < this.trail.length; i++) {
                ctx.lineTo(this.trail[i].x, this.trail[i].y);
            }
            ctx.stroke();
        }
        
        // Draw projectile
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
        ctx.fill();
        
        // Draw outline
        ctx.strokeStyle = "#FF5252";
        ctx.lineWidth = 2;
        ctx.stroke();
    }
    
    drawPrediction(ctx) {
        // Draw predicted trajectory
        ctx.strokeStyle = "rgba(76, 201, 240, 0.3)";
        ctx.lineWidth = 1;
        ctx.setLineDash([5, 5]);
        ctx.beginPath();
        
        if (this.predictedTrajectory.length > 0) {
            ctx.moveTo(this.predictedTrajectory[0].x, 
                      this.predictedTrajectory[0].y);
            
            for (let i = 1; i < this.predictedTrajectory.length; i++) {
                ctx.lineTo(this.predictedTrajectory[i].x, 
                          this.predictedTrajectory[i].y);
            }
        }
        
        ctx.stroke();
        ctx.setLineDash([]);
    }
    
    createImpactEffect() {
        // Create explosion/impact particles
        console.log("Impact at:", this.x, this.y);
    }
}
