class CannonGame {
    constructor(canvasId) {
        this.canvas = document.getElementById(canvasId);
        this.ctx = this.canvas.getContext("2d");
        
        // Game elements
        this.cannon = new Cannon(100, this.canvas.height - 50);
        this.targets = this.createTargets();
        this.projectiles = [];
        this.particles = [];
        
        // Game state
        this.score = 0;
        this.ammo = 20;
        this.wind = (Math.random() - 0.5) * 20; // Wind speed (-10 to 10)
        
        // UI
        this.showTrajectory = true;
        this.showGrid = true;
        
        // Physics
        this.physics = new ProjectilePhysics();
        
        // Controls
        this.setupControls();
        
        // Start game loop
        this.lastTime = 0;
        this.animate();
    }
    
    createTargets() {
        let targets = [];
        let targetCount = 5;
        
        for (let i = 0; i < targetCount; i++) {
            targets.push({
                x: 400 + i * 100,
                y: this.canvas.height - 100 - Math.random() * 50,
                width: 60,
                height: 40,
                color: "#FF6B6B",
                points: 100,
                health: 3,
                isDestroyed: false
            });
        }
        
        // Add moving target
        targets.push({
            x: 600,
            y: this.canvas.height - 150,
            width: 40,
            height: 40,
            color: "#4ECDC4",
            points: 200,
            health: 5,
            isDestroyed: false,
            speed: 1,
            direction: 1
        });
        
        return targets;
    }
    
    update(deltaTime) {
        let dt = deltaTime / 1000;
        
        // Update cannon
        this.cannon.update(dt);
        
        // Update projectiles
        for (let i = this.projectiles.length - 1; i >= 0; i--) {
            let projectile = this.projectiles[i];
            projectile.update(dt);
            
            // Apply wind effect
            projectile.vx += this.wind * dt;
            
            // Check collisions
            if (this.checkCollisions(projectile)) {
                this.projectiles.splice(i, 1);
                continue;
            }
            
            // Remove if off screen
            if (projectile.x > this.canvas.width || 
                projectile.x < 0 || 
                projectile.y > this.canvas.height) {
                this.projectiles.splice(i, 1);
            }
        }
        
        // Update targets
        this.updateTargets(dt);
        
        // Update particles
        this.updateParticles(dt);
    }
    
    updateTargets(dt) {
        for (let target of this.targets) {
            if (target.isDestroyed) continue;
            
            // Move moving target
            if (target.speed) {
                target.x += target.speed * target.direction * dt * 50;
                
                // Bounce off edges
                if (target.x < 400 || target.x > this.canvas.width - 50) {
                    target.direction *= -1;
                }
            }
        }
    }
    
    checkCollisions(projectile) {
        for (let target of this.targets) {
            if (target.isDestroyed) continue;
            
            // Simple circle-rectangle collision
            let closestX = Math.max(target.x, 
                                   Math.min(projectile.x, target.x + target.width));
            let closestY = Math.max(target.y, 
                                   Math.min(projectile.y, target.y + target.height));
            
            let distanceX = projectile.x - closestX;
            let distanceY = projectile.y - closestY;
            let distance = Math.sqrt(distanceX * distanceX + distanceY * distanceY);
            
            if (distance < projectile.radius) {
                // Hit!
                target.health--;
                
                if (target.health <= 0) {
                    target.isDestroyed = true;
                    this.score += target.points;
                    this.createExplosion(target.x + target.width/2, 
                                       target.y + target.height/2);
                } else {
                    // Hit but not destroyed
                    this.createHitEffect(target.x + target.width/2, 
                                        target.y + target.height/2);
                }
                
                return true;
            }
        }
        
        return false;
    }
    
    fireCannon() {
        if (this.ammo <= 0) return;
        
        this.ammo--;
        
        // Calculate projectile starting position (at cannon barrel end)
        let angleRad = this.cannon.angle * Math.PI / 180;
        let barrelLength = 40;
        let startX = this.cannon.x + Math.cos(angleRad) * barrelLength;
        let startY = this.cannon.y + Math.sin(angleRad) * barrelLength;
        
        // Create projectile
        let projectile = new CannonProjectile(
            startX,
            startY,
            this.cannon.power,
            this.cannon.angle
        );
        
        this.projectiles.push(projectile);
        
        // Create muzzle flash
        this.createMuzzleFlash(startX, startY, angleRad);
    }
    
    createMuzzleFlash(x, y, angle) {
        for (let i = 0; i < 20; i++) {
            this.particles.push({
                x: x,
                y: y,
                vx: Math.cos(angle) * 100 + (Math.random() - 0.5) * 50,
                vy: Math.sin(angle) * 100 + (Math.random() - 0.5) * 50,
                life: 1.0,
                size: 2 + Math.random() * 4,
                color: "#FFD700"
            });
        }
    }
    
    createHitEffect(x, y) {
        for (let i = 0; i < 10; i++) {
            this.particles.push({
                x: x,
                y: y,
                vx: (Math.random() - 0.5) * 100,
                vy: (Math.random() - 0.5) * 100,
                life: 1.0,
                size: 1 + Math.random() * 3,
                color: "#FF6B6B"
            });
        }
    }
    
    createExplosion(x, y) {
        for (let i = 0; i < 30; i++) {
            this.particles.push({
                x: x,
                y: y,
                vx: (Math.random() - 0.5) * 200,
                vy: (Math.random() - 0.5) * 200,
                life: 1.0,
                size: 2 + Math.random() * 6,
                color: ["#FF6B6B", "#FFD166", "#FF9800"][Math.floor(Math.random() * 3)]
            });
        }
    }
    
    updateParticles(dt) {
        for (let i = this.particles.length - 1; i >= 0; i--) {
            let particle = this.particles[i];
            
            // Apply gravity
            particle.vy += 9.81 * dt;
            
            // Update position
            particle.x += particle.vx * dt;
            particle.y += particle.vy * dt;
            
            // Reduce life
            particle.life -= dt;
            
            // Remove dead particles
            if (particle.life <= 0) {
                this.particles.splice(i, 1);
            }
        }
    }
    
    draw() {
        // Clear canvas
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        
        // Draw background
        this.drawBackground();
        
        // Draw grid if enabled
        if (this.showGrid) this.drawGrid();
        
        // Draw targets
        this.drawTargets();
        
        // Draw projectiles
        for (let projectile of this.projectiles) {
            projectile.draw(this.ctx);
        }
        
        // Draw cannon
        this.cannon.draw(this.ctx);
        
        // Draw particles
        this.drawParticles();
        
        // Draw trajectory prediction
        if (this.showTrajectory) {
            this.drawTrajectoryPrediction();
        }
        
        // Draw UI
