    drawTrajectoryPrediction() {
        // Calculate predicted trajectory
        let angleRad = this.cannon.angle * Math.PI / 180;
        let barrelLength = 40;
        let startX = this.cannon.x + Math.cos(angleRad) * barrelLength;
        let startY = this.cannon.y + Math.sin(angleRad) * barrelLength;
        
        let trajectory = this.physics.calculateTrajectory(
            startX, startY, this.cannon.power, this.cannon.angle, 30
        );
        
        // Apply wind effect to prediction
        for (let point of trajectory) {
            // Simple wind effect on trajectory
            let time = (point.x - startX) / (this.cannon.power * Math.cos(angleRad));
            point.x += this.wind * time * time * 0.5;
