// Advanced projectile types
class AdvancedProjectile extends CannonProjectile {
    constructor(x, y, speed, angle, type = "normal") {
        super(x, y, speed, angle);
        this.type = type;
        
        // Set properties based on type
        switch(type) {
            case "explosive":
                this.color = "#FF9800";
                this.radius = 12;
                this.explosionRadius = 80;
                this.damage = 50;
                break;
                
            case "bouncing":
                this.color = "#4CAF50";
                this.radius = 8;
                this.bouncesRemaining = 3;
                this.bounceEnergyLoss = 0.7;
                break;
                
            case "guided":
                this.color = "#2196F3";
                this.radius = 10;
                this.homingStrength = 0.1;
                this.target = null;
                break;
                
            case "cluster":
                this.color = "#9C27B0";
                this.radius = 15;
                this.clusterCount = 5;
                this.clusterDelay = 1.0; // seconds
                break;
        }
    }
    
    update(deltaTime) {
        super.update(deltaTime);
        
        // Type-specific behavior
        switch(this.type) {
            case "guided":
                this.homingBehavior(deltaTime);
                break;
                
            case "cluster":
                this.clusterBehavior(deltaTime);
                break;
        }
    }
    
    homingBehavior(dt) {
        if (!this.target || this.target.isDestroyed) return;
        
        // Calculate direction to target
        let dx = this.target.x - this.x;
        let dy = this.target.y - this.y;
        let distance = Math.sqrt(dx * dx + dy * dy);
        
        if (distance < 1) return;
        
        // Normalize direction
        dx /= distance;
        dy /= distance;
        
        // Adjust velocity towards target
        let currentSpeed = Math.sqrt(this.vx * this.vx + this.vy * this.vy);
        let homingFactor = this.homingStrength * dt;
        
        this.vx += dx * currentSpeed * homingFactor;
        this.vy += dy * currentSpeed * homingFactor;
        
        // Maintain overall speed
        let newSpeed = Math.sqrt(this.vx * this.vx + this.vy * this.vy);
        let speedRatio = currentSpeed / newSpeed;
        
        this.vx *= speedRatio;
        this.vy *= speedRatio;
    }
    
    clusterBehavior(dt) {
        this.clusterDelay -= dt / 1000;
        
        if (this.clusterDelay <= 0 && this.clusterCount > 0) {
            // Create cluster bomblets
            for (let i = 0; i < 5; i++) {
                let angle = (i / 5) * Math.PI * 2;
                let speed = 50;
                
                let bomblet = new CannonProjectile(
                    this.x,
                    this.y,
                    speed,
                    angle * 180 / Math.PI
                );
                bomblet.radius = 5;
                bomblet.color = "#9C27B0";
                
                // Add to game projectiles array
                game.projectiles.push(bomblet);
            }
            
            this.clusterCount--;
            this.clusterDelay = 0.5; // Delay between clusters
        }
    }
    
    onGroundHit() {
        switch(this.type) {
            case "explosive":
                this.createExplosion();
                break;
                
            case "bouncing":
                if (this.bouncesRemaining > 0) {
                    this.bouncesRemaining--;
                    this.vy = -Math.abs(this.vy) * this.bounceEnergyLoss;
                    this.y = 0; // Reset to ground level
                    return false; // Don't destroy yet
                }
                break;
        }
        
        return true; // Destroy projectile
    }
    
    createExplosion() {
        // Create large explosion effect
        for (let i = 0; i < 50; i++) {
            let angle = Math.random() * Math.PI * 2;
            let speed = 50 + Math.random() * 100;
            
            game.particles.push({
                x: this.x,
                y: this.y,
                vx: Math.cos(angle) * speed,
                vy: Math.sin(angle) * speed,
                life: 1.5,
                size: 3 + Math.random() * 7,
                color: ["#FF6B6B", "#FFD166", "#FF9800"][Math.floor(Math.random() * 3)]
            });
        }
        
        // Damage nearby targets
        for (let target of game.targets) {
            if (target.isDestroyed) continue;
            
            let dx = target.x - this.x;
            let dy = target.y - this.y;
            let distance = Math.sqrt(dx * dx + dy * dy);
            
            if (distance < this.explosionRadius) {
                // Damage based on distance
                let damage = this.damage * (1 - distance / this.explosionRadius);
                target.health -= damage;
                
                if (target.health <= 0) {
                    target.isDestroyed = true;
                    game.score += target.points;
                }
            }
        }
    }
}

// Wind simulation
class WindSimulation {
    constructor() {
        this.speed = 0;
        this.direction = 1; // 1 = right, -1 = left
        this.turbulence = 0.1;
        this.baseChangeRate = 0.01;
    }
    
    update(dt) {
        // Slowly change wind speed and direction
        let change = (Math.random() - 0.5) * this.baseChangeRate * dt;
        this.speed += change;
        
        // Add turbulence
        this.speed += (Math.random() - 0.5) * this.turbulence;
        
        // Keep within bounds
        this.speed = Math.max(-20, Math.min(20, this.speed));
        
        // Occasionally change direction
        if (Math.random() < 0.001 * dt) {
            this.direction *= -1;
        }
    }
    
    getWindForce(mass, area) {
        // Calculate wind force on object
        // F = 0.5 * Ï * vÂ² * A * Cd
        let airDensity = 1.225; // kg/mÂ³
        let dragCoefficient = 0.47; // Sphere
        
        let force = 0.5 * airDensity * 
                   this.speed * this.speed * 
                   area * dragCoefficient;
        
        return force * this.direction;
    }
    
    draw(ctx, canvas) {
        // Draw wind indicator
        ctx.fillStyle = "rgba(76, 201, 240, 0.5)";
        ctx.font = "bold 16px Arial";
        ctx.textAlign = "center";
        
        let windText = \`Wind: \${Math.abs(this.speed).toFixed(1)} m/s \`;
        windText += this.speed > 0 ? "â†’" : "â†";
        
        ctx.fillText(windText, canvas.width / 2, 30);
        
        // Draw wind particles
        let particleCount = 20;
        for (let i = 0; i < particleCount; i++) {
            let x = (i / particleCount) * canvas.width;
            let y = 50 + Math.sin(Date.now() / 500 + i) * 10;
            
            ctx.fillStyle = "rgba(76, 201, 240, 0.3)";
            ctx.beginPath();
            ctx.arc(x, y, 2, 0, Math.PI * 2);
            ctx.fill();
            
            // Move particles with wind
            x += this.speed * 0.1;
            if (x > canvas.width) x = 0;
            if (x < 0) x = canvas.width;
        }
    }
}
