class DinoGame {
    constructor(canvasId) {
        this.canvas = document.getElementById(canvasId);
        this.ctx = this.canvas.getContext("2d");
        
        // Game constants
        this.GROUND_Y = this.canvas.height - 50;
        this.GRAVITY = 0.5;
        this.JUMP_FORCE = -12;
        this.INITIAL_SPEED = 5;
        
        // Game state
        this.reset();
        
        // Game elements
        this.dino = new Dino(100, this.GROUND_Y);
        this.obstacles = [];
        this.clouds = [];
        this.particles = [];
        
        // Game timing
        this.lastTime = 0;
        this.obstacleTimer = 0;
        this.cloudTimer = 0;
        
        // Controls
        this.keys = {};
        this.setupControls();
        
        // Start game loop
        this.animate();
    }
    
    reset() {
        this.gameState = "READY"; // READY, PLAYING, GAME_OVER
        this.score = 0;
        this.highScore = localStorage.getItem("dinoHighScore") || 0;
        this.speed = this.INITIAL_SPEED;
        this.gameTime = 0;
        this.obstacles = [];
        this.clouds = [];
        this.particles = [];
        this.isDay = true;
        this.dayNightTimer = 0;
        
        // Reset dino
        if (this.dino) {
            this.dino.reset();
            this.dino.y = this.GROUND_Y;
        }
    }
    
    update(deltaTime) {
        if (this.gameState !== "PLAYING") return;
        
        let dt = deltaTime / 16.67; // Normalize to 60fps
        
        this.gameTime += dt;
        this.score += dt * this.speed * 0.1;
        
        // Increase speed over time
        this.speed = this.INITIAL_SPEED + Math.floor(this.gameTime / 100) * 0.5;
        
        // Day/night cycle (every 1000 points)
        this.dayNightTimer += dt;
        if (this.dayNightTimer > 1000) {
            this.isDay = !this.isDay;
            this.dayNightTimer = 0;
        }
        
        // Update dino
        this.dino.update(dt, this.GRAVITY, this.GROUND_Y);
        
        // Update obstacles
        this.updateObstacles(dt);
        
        // Update clouds
        this.updateClouds(dt);
        
        // Update particles
        this.updateParticles(dt);
        
        // Generate new obstacles
        this.obstacleTimer += dt;
        if (this.obstacleTimer > this.getObstacleInterval()) {
            this.generateObstacle();
            this.obstacleTimer = 0;
        }
        
        // Generate clouds
        this.cloudTimer += dt;
        if (this.cloudTimer > 100 + Math.random() * 200) {
            this.generateCloud();
            this.cloudTimer = 0;
        }
        
        // Check collisions
        this.checkCollisions();
    }
    
    getObstacleInterval() {
        // Decrease interval as speed increases
        let baseInterval = 60;
