        if (this.isDay) {
            this.ctx.fillStyle = "#87CEEB"; // Day sky
        } else {
            this.ctx.fillStyle = "#0F1B2F"; // Night sky
            
            // Draw stars at night
            this.ctx.fillStyle = "white";
            for (let i = 0; i < 50; i++) {
