                let y = (i * 19) % 200;
                let size = 1 + Math.sin(this.gameTime * 0.01 + i) * 0.5;
                
                this.ctx.beginPath();
                this.ctx.arc(x, y, size, 0, Math.PI * 2);
                this.ctx.fill();
