        }
        
        this.ctx.fillRect(0, 0, this.canvas.width, this.GROUND_Y);
    }
    
    drawGround() {
        // Ground color
        this.ctx.fillStyle = this.isDay ? "#8B4513" : "#5D4037";
        this.ctx.fillRect(0, this.GROUND_Y, this.canvas.width, 50);
        
        // Ground pattern (lines)
        this.ctx.strokeStyle = this.isDay ? "#A0522D" : "#795548";
        this.ctx.lineWidth = 2;
        
        let lineSpacing = 20;
        let offset = (this.gameTime * this.speed) % lineSpacing;
        
        for (let x = -offset; x < this.canvas.width; x += lineSpacing) {
            this.ctx.beginPath();
            this.ctx.moveTo(x, this.GROUND_Y);
            this.ctx.lineTo(x + 10, this.GROUND_Y);
            this.ctx.stroke();
