            if (this.gameState === "GAME_OVER" && e.key === " ") {
                this.reset();
                this.gameState = "PLAYING";
            }
            
            if (e.key === "r" || e.key === "R") {
                this.reset();
                if (this.gameState === "READY") {
                    this.gameState = "PLAYING";
                }
            }
        });
        
        document.addEventListener("keyup", (e) => {
            this.keys[e.key.toLowerCase()] = false;
            
            if (e.key === "ArrowDown") {
                this.dino.duck(false);
            }
        });
        
        // Touch/mobile controls
        this.canvas.addEventListener("touchstart", (e) => {
            e.preventDefault();
            
            if (this.gameState === "READY") {
                this.gameState = "PLAYING";
                return;
            }
            
            if (this.gameState === "PLAYING") {
                // Jump on top half tap, duck on bottom half
                let touchY = e.touches[0].clientY - this.canvas.getBoundingClientRect().top;
                
                if (touchY < this.canvas.height / 2) {
                    this.dino.jump(this.JUMP_FORCE);
                } else {
                    this.dino.duck(true);
                }
            }
            
            if (this.gameState === "GAME_OVER") {
                this.reset();
                this.gameState = "PLAYING";
            }
        });
        
        this.canvas.addEventListener("touchend", (e) => {
            e.preventDefault();
            this.dino.duck(false);
        });
    }
    
    animate(timestamp = 0) {
        let deltaTime = timestamp - this.lastTime;
        this.lastTime = timestamp;
        
        this.update(deltaTime);
        this.draw();
        
        requestAnimationFrame((ts) => this.animate(ts));
    }
