            <pre style="background: white; padding: 15px; border-radius: 5px; max-height: 500px; overflow-y: auto;">
class Dino {
    constructor(x, groundY) {
        this.x = x;
        this.y = groundY;
        this.groundY = groundY;
        
        // Physics
        this.velocityY = 0;
        this.isJumping = false;
        this.isDucking = false;
        
        // Dimensions
        this.width = 40;
        this.height = 60;
        this.duckHeight = 30;
        
        // Animation
        this.runFrame = 0;
        this.runSpeed = 0.2;
        this.duckFrame = 0;
        
        // State
        this.isAlive = true;
    }
    
    reset() {
        this.y = this.groundY;
        this.velocityY = 0;
        this.isJumping = false;
        this.isDucking = false;
        this.isAlive = true;
        this.runFrame = 0;
        this.duckFrame = 0;
    }
    
    update(dt, gravity, groundY) {
        this.groundY = groundY;
        
        // Apply gravity
        if (this.isJumping) {
            this.velocityY += gravity;
            this.y += this.velocityY;
            
            // Hit ground
            if (this.y >= this.groundY) {
                this.y = this.groundY;
                this.velocityY = 0;
                this.isJumping = false;
            }
        }
        
        // Update animation frames
        if (!this.isJumping && !this.isDucking) {
            this.runFrame += this.runSpeed * dt;
            if (this.runFrame >= 4) this.runFrame = 0;
        }
        
        if (this.isDucking) {
            this.duckFrame += this.runSpeed * dt;
            if (this.duckFrame >= 2) this.duckFrame = 0;
        }
    }
    
    jump(force) {
        if (!this.isJumping && !this.isDucking) {
            this.velocityY = force;
            this.isJumping = true;
        }
    }
    
    duck(isDucking) {
        if (!this.isJumping) {
            this.isDucking = isDucking;
        }
    }
    
    collidesWith(obstacle) {
        if (!this.isAlive) return false;
        
        // Get current dino dimensions
        let dinoHeight = this.isDucking ? this.duckHeight : this.height;
        let dinoY = this.groundY - dinoHeight;
        
        // Simple AABB collision
        return (
            this.x < obstacle.x + obstacle.width &&
            this.x + this.width > obstacle.x &&
            dinoY < obstacle.y + obstacle.height &&
            dinoY + dinoHeight > obstacle.y
        );
    }
    
    draw(ctx, overrideX = null, overrideY = null) {
        let x = overrideX !== null ? overrideX : this.x;
        let y = overrideY !== null ? overrideY : this.groundY - (this.isDucking ? this.duckHeight : this.height);
        
        ctx.save();
        
        if (this.isJumping) {
            this.drawJumping(ctx, x, y);
        } else if (this.isDucking) {
            this.drawDucking(ctx, x, y);
        } else {
            this.drawRunning(ctx, x, y);
        }
        
        // Debug collision box
        if (DEBUG_MODE) {
            ctx.strokeStyle = "red";
            ctx.lineWidth = 1;
            ctx.strokeRect(x, y, this.width, this.isDucking ? this.duckHeight : this.height);
        }
        
        ctx.restore();
    }
    
    drawRunning(ctx, x, y) {
        let frame = Math.floor(this.runFrame);
        
        // Body
        ctx.fillStyle = "#2C3E50";
        ctx.fillRect(x + 10, y + 10, 20, 40);
        
        // Head
        ctx.fillRect(x + 30, y, 10, 20);
        
        // Legs based on frame
        ctx.fillStyle = "#34495E";
        
        switch(frame) {
            case 0:
                // Left leg forward
                ctx.fillRect(x + 12, y + 50, 6, 10);
                ctx.fillRect(x + 22, y + 50, 6, 10);
                break;
            case 1:
                // Both legs middle
                ctx.fillRect(x + 12, y + 50, 6, 10);
                ctx.fillRect(x + 22, y + 50, 6, 10);
                break;
            case 2:
                // Right leg forward
                ctx.fillRect(x + 12, y + 50, 6, 10);
                ctx.fillRect(x + 22, y + 50, 6, 10);
                break;
            case 3:
                // Both legs middle
                ctx.fillRect(x + 12, y + 50, 6, 10);
                ctx.fillRect(x + 22, y + 50, 6, 10);
                break;
        }
        
        // Eye
        ctx.fillStyle = "white";
        ctx.fillRect(x + 32, y + 5, 3, 3);
        
        // Smile
        ctx.strokeStyle = "white";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(x + 32, y + 12, 4, 0.2, Math.PI - 0.2);
        ctx.stroke();
    }
    
    drawJumping(ctx, x, y) {
        // Body
        ctx.fillStyle = "#2C3E50";
        ctx.fillRect(x + 10, y + 10, 20, 40);
        
        // Head
        ctx.fillRect(x + 30, y, 10, 20);
        
        // Legs (together when jumping)
        ctx.fillStyle = "#34495E";
        ctx.fillRect(x + 15, y + 50, 6, 10);
        ctx.fillRect(x + 20, y + 50, 6, 10);
        
        // Eye
        ctx.fillStyle = "white";
        ctx.fillRect(x + 32, y + 5, 3, 3);
        
        // Excited smile
        ctx.strokeStyle = "white";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(x + 32, y + 12, 5, 0.1, Math.PI - 0.1);
        ctx.stroke();
    }
    
    drawDucking(ctx, x, y) {
        let frame = Math.floor(this.duckFrame);
        
        // Low body
        ctx.fillStyle = "#2C3E50";
        ctx.fillRect(x + 10, y + 20, 30, 20);
        
        // Head (lower)
        ctx.fillRect(x + 30, y + 10, 10, 15);
        
        // Legs position based on frame
        ctx.fillStyle = "#34495E";
        
        if (frame === 0) {
            ctx.fillRect(x + 12, y + 40, 8, 10);
            ctx.fillRect(x + 25, y + 40, 8, 10);
        } else {
            ctx.fillRect(x + 15, y + 40, 8, 10);
            ctx.fillRect(x + 22, y + 40, 8, 10);
        }
        
        // Eye
        ctx.fillStyle = "white";
        ctx.fillRect(x + 32, y + 15, 3, 3);
        
        // Determined smile
        ctx.strokeStyle = "white";
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(x + 30, y + 22);
        ctx.lineTo(x + 35, y + 22);
        ctx.stroke();
    }
}

class Cactus {
    constructor(x, groundY, type = "small") {
        this.x = x;
        this.groundY = groundY;
        this.type = type;
        
        // Set dimensions based on type
        switch(type) {
            case "small":
                this.width = 20;
                this.height = 40;
                this.y = groundY - this.height;
                this.color = "#27AE60";
                break;
                
            case "double":
                this.width = 40;
                this.height = 50;
                this.y = groundY - this.height;
                this.color = "#229954";
                break;
                
            case "triple":
                this.width = 60;
                this.height = 45;
                this.y = groundY - this.height;
                this.color = "#1E8449";
                break;
        }
    }
    
    update(dt, gameSpeed) {
        this.x -= gameSpeed * dt;
    }
    
    draw(ctx) {
        ctx.save();
        
        // Draw based on type
        switch(this.type) {
            case "small":
                this.drawSmallCactus(ctx);
                break;
                
            case "double":
                this.drawDoubleCactus(ctx);
                break;
                
            case "triple":
                this.drawTripleCactus(ctx);
                break;
        }
        
        // Debug collision box
        if (DEBUG_MODE) {
            ctx.strokeStyle = "red";
            ctx.lineWidth = 1;
            ctx.strokeRect(this.x, this.y, this.width, this.height);
        }
        
        ctx.restore();
    }
    
    drawSmallCactus(ctx) {
        // Main stem
        ctx.fillStyle = this.color;
        ctx.fillRect(this.x + 6, this.y, 8, this.height);
        
        // Arms
        ctx.fillRect(this.x, this.y + 10, 6, 6);
        ctx.fillRect(this.x + 14, this.y + 20, 6, 6);
        
        // Spikes
        this.drawSpikes(ctx, this.x + 6, this.y, 8, this.height);
    }
    
    drawDoubleCactus(ctx) {
        // Left stem
        ctx.fillStyle = this.color;
        ctx.fillRect(this.x + 6, this.y + 10, 8, this.height - 10);
        
        // Right stem
        ctx.fillRect(this.x + 26, this.y, 8, this.height);
        
        // Connecting arms
        ctx.fillRect(this.x + 14, this.y + 20, 12, 6);
        
        // Spikes
        this.drawSpikes(ctx, this.x + 6, this.y + 10, 8, this.height - 10);
        this.drawSpikes(ctx, this.x + 26, this.y, 8, this.height);
    }
    
    drawTripleCactus(ctx) {
        // Left stem (short)
        ctx.fillStyle = this.color;
        ctx.fillRect(this.x + 6, this.y + 15, 8, this.height - 15);
        
        // Middle stem (tall)
        ctx.fillRect(this.x + 26, this.y, 8, this.height);
        
        // Right stem (medium)
        ctx.fillRect(this.x + 46, this.y + 8, 8, this.height - 8);
        
        // Connecting arms
        ctx.fillRect(this.x + 14, this.y + 25, 12, 6);
        ctx.fillRect(this.x + 34, this.y + 15, 12, 6);
        
        // Spikes
        this.drawSpikes(ctx, this.x + 6, this.y + 15, 8, this.height - 15);
        this.drawSpikes(ctx, this.x + 26, this.y, 8, this.height);
        this.drawSpikes(ctx, this.x + 46, this.y + 8, 8, this.height - 8);
    }
    
    drawSpikes(ctx, x, y, width, height) {
        ctx.strokeStyle = "#145A32";
        ctx.lineWidth = 2;
        
        // Draw spikes along the edges
        let spikeSpacing = 8;
        for (let i = y + 4; i < y + height; i += spikeSpacing) {
            // Left side spikes
            ctx.beginPath();
            ctx.moveTo(x, i);
            ctx.lineTo(x - 3, i + 2);
            ctx.stroke();
            
            // Right side spikes
            ctx.beginPath();
            ctx.moveTo(x + width, i);
            ctx.lineTo(x + width + 3, i + 2);
            ctx.stroke();
        }
    }
}

class Bird {
    constructor(x, y) {
        this.x = x;
        this.y = y;
        this.width = 40;
        this.height = 20;
        
        // Animation
        this.flapFrame = 0;
        this.flapSpeed = 0.3;
        this.wingOffset = 0;
        
        // Color
        this.color = "#E74C3C";
    }
    
    update(dt, gameSpeed) {
        this.x -= gameSpeed * dt;
        
        // Flap wings
        this.flapFrame += this.flapSpeed * dt;
        this.wingOffset = Math.sin(this.flapFrame) * 5;
    }
    
    draw(ctx) {
        ctx.save();
        
        // Body
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.ellipse(
            this.x + this.width / 2,
            this.y + this.height / 2,
            this.width / 3,
            this.height / 2,
            0, 0, Math.PI * 2
        );
        ctx.fill();
        
        // Head
        ctx.beginPath();
        ctx.arc(this.x + this.width - 10, this.y + this.height / 2, 8, 0, Math.PI * 2);
        ctx.fill();
        
        // Beak
        ctx.fillStyle = "#F1C40F";
        ctx.beginPath();
        ctx.moveTo(this.x + this.width, this.y + this.height / 2);
        ctx.lineTo(this.x + this.width + 10, this.y + this.height / 2);
        ctx.lineTo(this.x + this.width, this.y + this.height / 2 + 5);
        ctx.fill();
        
        // Eye
        ctx.fillStyle = "white";
        ctx.beginPath();
        ctx.arc(this.x + this.width - 5, this.y + this.height / 2 - 3, 2, 0, Math.PI * 2);
        ctx.fill();
        
        // Wings
        ctx.fillStyle = "#C0392B";
        this.drawWing(ctx, this.x + 10, this.y + this.height / 2 + this.wingOffset);
        this.drawWing(ctx, this.x + 15, this.y + this.height / 2 - this.wingOffset);
        
        // Debug collision box
        if (DEBUG_MODE) {
            ctx.strokeStyle = "red";
            ctx.lineWidth = 1;
            ctx.strokeRect(this.x, this.y, this.width, this.height);
        }
        
        ctx.restore();
    }
    
    drawWing(ctx, x, y) {
        ctx.beginPath();
        ctx.moveTo(x, y);
        ctx.quadraticCurveTo(x - 10, y - 15, x - 5, y - 25);
        ctx.quadraticCurveTo(x + 5, y - 15, x, y);
        ctx.fill();
    }
}

class Particle {
    constructor(x, y, vx, vy, life) {
        this.x = x;
        this.y = y;
        this.vx = vx;
        this.vy = vy;
        this.life = life;
        this.maxLife = life;
        this.size = 2 + Math.random() * 4;
        this.color = \`hsl(\${Math.random() * 60}, 100%, 50%)\`; // Yellow to red
    }
    
    update(dt) {
        this.x += this.vx * (dt / 16.67);
        this.vy += 0.2 * (dt / 16.67); // Gravity
        this.y += this.vy * (dt / 16.67);
        this.life -= dt / 16.67;
        this.size *= 0.98;
    }
    
    draw(ctx) {
        ctx.globalAlpha = this.life / this.maxLife;
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();
        ctx.globalAlpha = 1;
    }
}
