// Sprite with transformations
class AdvancedSprite {
    constructor(image, x, y) {
        this.image = image;
        this.x = x;
        this.y = y;
        this.width = image.width;
        this.height = image.height;
        
        // Transformations
        this.scaleX = 1;
        this.scaleY = 1;
        this.rotation = 0; // radians
        this.opacity = 1;
        this.flipX = false;
        this.flipY = false;
        
        // Color tint
        this.tintColor = null;
        this.tintAmount = 0;
    }
    
    draw(ctx) {
        if (!this.image) return;
        
        ctx.save();
        
        // Move to sprite position
        ctx.translate(this.x, this.y);
        
        // Apply rotation
        if (this.rotation !== 0) {
            ctx.rotate(this.rotation);
        }
        
        // Apply scaling (with flip)
        let scaleX = this.scaleX * (this.flipX ? -1 : 1);
        let scaleY = this.scaleY * (this.flipY ? -1 : 1);
        ctx.scale(scaleX, scaleY);
        
        // Apply opacity
        ctx.globalAlpha = this.opacity;
        
        // Draw image centered
        const drawX = -this.width / 2;
        const drawY = -this.height / 2;
        
        if (this.tintColor && this.tintAmount > 0) {
            // Draw with color tint
            this.drawWithTint(ctx, drawX, drawY);
        } else {
            // Draw normally
            ctx.drawImage(
                this.image,
                drawX,
                drawY,
                this.width,
                this.height
            );
        }
        
        ctx.restore();
    }
    
    drawWithTint(ctx, x, y) {
        // Create temporary canvas for tint effect
        const tempCanvas = document.createElement('canvas');
        tempCanvas.width = this.width;
        tempCanvas.height = this.height;
        const tempCtx = tempCanvas.getContext('2d');
        
        // Draw original image
        tempCtx.drawImage(this.image, 0, 0);
        
        // Apply tint color
        tempCtx.globalCompositeOperation = 'source-atop';
        tempCtx.fillStyle = this.tintColor;
        tempCtx.globalAlpha = this.tintAmount;
        tempCtx.fillRect(0, 0, this.width, this.height);
        
        // Reset composite operation and draw original underneath
        tempCtx.globalCompositeOperation = 'destination-over';
        tempCtx.globalAlpha = 1;
        tempCtx.drawImage(this.image, 0, 0);
        
        // Draw to main canvas
        ctx.drawImage(tempCanvas, x, y, this.width, this.height);
    }
    
    // Utility methods
    setScale(scale) {
        this.scaleX = scale;
        this.scaleY = scale;
    }
    
    setRotation(degrees) {
        this.rotation = degrees * Math.PI / 180;
    }
    
    setTint(color, amount = 0.5) {
        this.tintColor = color;
        this.tintAmount = amount;
    }
    
    setFlip(horizontal = false, vertical = false) {
        this.flipX = horizontal;
        this.flipY = vertical;
    }
}

// Pre-rendering for performance
class SpriteCache {
    constructor() {
        this.cache = new Map();
    }
    
    // Create and cache a pre-rendered version of sprite
    getCachedSprite(sprite, width, height) {
        const cacheKey = \`\${sprite.image.src}_\${width}x\${height}\`;
        
        if (this.cache.has(cacheKey)) {
            return this.cache.get(cacheKey);
        }
        
        // Create new cached version
        const canvas = document.createElement('canvas');
        canvas.width = width;
        canvas.height = height;
        const ctx = canvas.getContext('2d');
        
        // Draw sprite to cache
        ctx.drawImage(sprite.image, 0, 0, width, height);
        
        this.cache.set(cacheKey, canvas);
        return canvas;
    }
    
    // Clear cache
    clear() {
        this.cache.clear();
    }
    
    // Get cache size
    get size() {
        return this.cache.size;
    }
}

// Image effects and filters
class ImageEffects {
    static applyFilter(ctx, image, filter) {
        const tempCanvas = document.createElement('canvas');
        tempCanvas.width = image.width;
        tempCanvas.height = image.height;
        const tempCtx = tempCanvas.getContext('2d');
        
        // Draw image
        tempCtx.drawImage(image, 0, 0);
        
        // Apply filter
        switch(filter) {
            case 'grayscale':
                this.applyGrayscale(tempCtx, tempCanvas.width, tempCanvas.height);
                break;
            case 'sepia':
                this.applySepia(tempCtx, tempCanvas.width, tempCanvas.height);
                break;
            case 'invert':
                this.applyInvert(tempCtx, tempCanvas.width, tempCanvas.height);
                break;
            case 'blur':
                this.applyBlur(tempCtx, tempCanvas.width, tempCanvas.height);
                break;
        }
        
        // Draw filtered image
        ctx.drawImage(tempCanvas, 0, 0);
    }
    
    static applyGrayscale(ctx, width, height) {
        const imageData = ctx.getImageData(0, 0, width, height);
        const data = imageData.data;
        
        for (let i = 0; i < data.length; i += 4) {
            const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
            data[i] = avg;     // red
            data[i + 1] = avg; // green
            data[i + 2] = avg; // blue
        }
        
        ctx.putImageData(imageData, 0, 0);
    }
    
    static applySepia(ctx, width, height) {
        const imageData = ctx.getImageData(0, 0, width, height);
        const data = imageData.data;
        
        for (let i = 0; i < data.length; i += 4) {
            const r = data[i];
            const g = data[i + 1];
            const b = data[i + 2];
            
            data[i] = Math.min(255, (r * 0.393) + (g * 0.769) + (b * 0.189));
            data[i + 1] = Math.min(255, (r * 0.349) + (g * 0.686) + (b * 0.168));
            data[i + 2] = Math.min(255, (r * 0.272) + (g * 0.534) + (b * 0.131));
        }
        
        ctx.putImageData(imageData, 0, 0);
    }
    
    static applyInvert(ctx, width, height) {
        const imageData = ctx.getImageData(0, 0, width, height);
        const data = imageData.data;
        
        for (let i = 0; i < data.length; i += 4) {
            data[i] = 255 - data[i];     // red
            data[i + 1] = 255 - data[i + 1]; // green
            data[i + 2] = 255 - data[i + 2]; // blue
        }
        
        ctx.putImageData(imageData, 0, 0);
    }
    
    static applyBlur(ctx, width, height) {
        // Simple box blur
        const imageData = ctx.getImageData(0, 0, width, height);
        const blurredData = this.boxBlur(imageData, 1);
        ctx.putImageData(blurredData, 0, 0);
    }
    
    static boxBlur(imageData, radius) {
        const data = imageData.data;
        const width = imageData.width;
        const height = imageData.height;
        
        const result = new Uint8ClampedArray(data.length);
        
        for (let y = 0; y < height; y++) {
            for (let x = 0; x < width; x++) {
                let r = 0, g = 0, b = 0, a = 0, count = 0;
                
                for (let dy = -radius; dy <= radius; dy++) {
                    for (let dx = -radius; dx <= radius; dx++) {
                        const nx = x + dx;
                        const ny = y + dy;
                        
                        if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                            const idx = (ny * width + nx) * 4;
                            r += data[idx];
                            g += data[idx + 1];
                            b += data[idx + 2];
                            a += data[idx + 3];
                            count++;
                        }
                    }
                }
                
                const idx = (y * width + x) * 4;
                result[idx] = r / count;
                result[idx + 1] = g / count;
                result[idx + 2] = b / count;
                result[idx + 3] = a / count;
            }
        }
        
        return new ImageData(result, width, height);
    }
}
