// Animation manager
class AnimationManager {
    constructor(spriteSheet, frameWidth, frameHeight) {
        this.spriteSheet = spriteSheet;
        this.frameWidth = frameWidth;
        this.frameHeight = frameHeight;
        this.currentAnimation = null;
        this.currentFrame = 0;
        this.frameTimer = 0;
        this.isPlaying = false;
        this.onComplete = null;
    }
    
    play(animationName) {
        const anim = this.animations[animationName];
        if (!anim) return;
        
        this.currentAnimation = anim;
        this.currentFrame = 0;
        this.frameTimer = 0;
        this.isPlaying = true;
    }
    
    update(deltaTime) {
        if (!this.isPlaying || !this.currentAnimation) return;
        
        this.frameTimer += deltaTime;
        const frameDuration = 1 / this.currentAnimation.speed;
        
        if (this.frameTimer >= frameDuration) {
            this.frameTimer = 0;
            this.currentFrame++;
            
            if (this.currentFrame >= this.currentAnimation.frames.length) {
                if (this.currentAnimation.loop === false) {
                    this.isPlaying = false;
                    if (this.onComplete) this.onComplete();
                } else {
                    this.currentFrame = 0;
                }
            }
        }
    }
    
    getCurrentFrame() {
        if (!this.currentAnimation) return 0;
        
        const frameIndex = this.currentAnimation.frames[this.currentFrame];
        const col = frameIndex % (this.spriteSheet.width / this.frameWidth);
        const row = Math.floor(frameIndex / (this.spriteSheet.width / this.frameWidth));
        
        return {
            x: col * this.frameWidth,
            y: row * this.frameHeight,
            width: this.frameWidth,
            height: this.frameHeight
        };
    }
    
    draw(ctx, x, y) {
        const frame = this.getCurrentFrame();
        
        ctx.drawImage(
            this.spriteSheet,
            frame.x, frame.y, frame.width, frame.height,
            x, y, frame.width, frame.height
        );
    }
}
