    }
    
    addTexture(name, image) {
        this.textures.set(name, image);
    }
    
    createEmitter(config) {
        const emitter = {
            x: config.x,
            y: config.y,
            rate: config.rate || 10, // particles per second
            burst: config.burst || 0,
            texture: config.texture || 'default',
            spawnArea: config.spawnArea || { type: 'point' },
            velocity: config.velocity || { min: 50, max: 100 },
            angle: config.angle || { min: 0, max: Math.PI * 2 },
            life: config.life || { min: 1, max: 3 },
            size: config.size || { start: 10, end: 0 },
            rotation: config.rotation || { start: 0, end: 0, speed: 0 },
            color: config.color || { start: '#FFFFFF', end: '#FFFFFF' },
            opacity: config.opacity || { start: 1, end: 0 },
            gravity: config.gravity || 0,
            active: true,
            timer: 0
        };
        
        this.emitters.push(emitter);
        
        // Initial burst
        if (emitter.burst > 0) {
            for (let i = 0; i < emitter.burst; i++) {
                this.createParticle(emitter);
            }
        }
        
        return emitter;
    }
    
    createParticle(emitter) {
        const texture = this.textures.get(emitter.texture);
        
        // Calculate spawn position
        let x = emitter.x;
        let y = emitter.y;
        
        if (emitter.spawnArea.type === 'circle') {
            const angle = Math.random() * Math.PI * 2;
            const radius = Math.random() * emitter.spawnArea.radius;
            x += Math.cos(angle) * radius;
            y += Math.sin(angle) * radius;
        } else if (emitter.spawnArea.type === 'rectangle') {
            x += (Math.random() - 0.5) * emitter.spawnArea.width;
            y += (Math.random() - 0.5) * emitter.spawnArea.height;
        }
        
        // Calculate initial velocity
        const angle = emitter.angle.min + Math.random() * 
                     (emitter.angle.max - emitter.angle.min);
        const speed = emitter.velocity.min + Math.random() * 
                     (emitter.velocity.max - emitter.velocity.min);
        
        const particle = {
            x: x,
            y: y,
            vx: Math.cos(angle) * speed,
            vy: Math.sin(angle) * speed,
            life: emitter.life.min + Math.random() * 
                  (emitter.life.max - emitter.life.min),
            maxLife: 0,
            size: emitter.size.start,
            startSize: emitter.size.start,
            endSize: emitter.size.end,
            rotation: emitter.rotation.start,
            rotationSpeed: emitter.rotation.speed,
            startRotation: emitter.rotation.start,
            endRotation: emitter.rotation.end,
            color: emitter.color.start,
            startColor: emitter.color.start,
            endColor: emitter.color.end,
            opacity: emitter.opacity.start,
            startOpacity: emitter.opacity.start,
            endOpacity: emitter.opacity.end,
            gravity: emitter.gravity,
            texture: texture,
            active: true
        };
        
        particle.maxLife = particle.life;
        this.particles.push(particle);
        
        return particle;
    }
    
    update(deltaTime) {
        const dt = deltaTime / 1000;
        
        // Update emitters
        for (const emitter of this.emitters) {
            if (!emitter.active) continue;
            
            emitter.timer += dt;
            
            // Spawn new particles based on rate
            while (emitter.timer >= 1 / emitter.rate) {
                emitter.timer -= 1 / emitter.rate;
                this.createParticle(emitter);
            }
        }
        
        // Update particles
        for (let i = this.particles.length - 1; i >= 0; i--) {
            const p = this.particles[i];
            
            // Update physics
            p.vy += p.gravity * dt;
            p.x += p.vx * dt;
            p.y += p.vy * dt;
            
            // Update rotation
            p.rotation += p.rotationSpeed * dt;
            
            // Update life
            p.life -= dt;
            
            if (p.life <= 0) {
                this.particles.splice(i, 1);
                continue;
            }
            
            // Calculate interpolation factor
            const t = 1 - (p.life / p.maxLife);
            
            // Interpolate values
            p.size = p.startSize + (p.endSize - p.startSize) * t;
            p.opacity = p.startOpacity + (p.endOpacity - p.startOpacity) * t;
            
            // Interpolate color
            if (p.startColor !== p.endColor) {
                p.color = this.lerpColor(p.startColor, p.endColor, t);
            }
            
            // Interpolate rotation
            p.rotation = p.startRotation + (p.endRotation - p.startRotation) * t;
        }
    }
    
    lerpColor(color1, color2, t) {
        // Simple color interpolation (for hex colors)
        const c1 = this.hexToRgb(color1);
        const c2 = this.hexToRgb(color2);
        
        const r = Math.round(c1.r + (c2.r - c1.r) * t);
        const g = Math.round(c1.g + (c2.g - c1.g) * t);
        const b = Math.round(c1.b + (c2.b - c1.b) * t);
        
        return this.rgbToHex(r, g, b);
    }
    
    hexToRgb(hex) {
        const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
        return result ? {
            r: parseInt(result[1], 16),
            g: parseInt(result[2], 16),
            b: parseInt(result[3], 16)
        } : { r: 255, g: 255, b: 255 };
    }
    
    rgbToHex(r, g, b) {
        return "#" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
    }
    
    draw(ctx) {
        ctx.save();
        
        for (const p of this.particles) {
            if (!p.active) continue;
            
            ctx.save();
            ctx.translate(p.x, p.y);
            ctx.rotate(p.rotation);
            ctx.globalAlpha = p.opacity;
            
            if (p.texture) {
                // Draw sprite texture
                ctx.drawImage(
                    p.texture,
                    -p.size / 2,
                    -p.size / 2,
                    p.size,
                    p.size
                );
            } else {
                // Fallback: Draw colored circle
                ctx.fillStyle = p.color;
                ctx.beginPath();
                ctx.arc(0, 0, p.size / 2, 0, Math.PI * 2);
                ctx.fill();
            }
            
            ctx.restore();
        }
        
        ctx.restore();
    }
}
