<h3>Loading and Preparing Sprite Sheets</h3>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre style='background-color: #36454f; color: #fff; border-radius: 5px; padding: 10px; margin: 8px;'>
// =========== SPRITE SHEET CLASS ===========
class SpriteSheet {
    constructor(imagePath, frameWidth, frameHeight, columns, rows) {
        this.image = new Image();
        this.image.src = imagePath;
        
        // Sprite sheet properties
        this.frameWidth = frameWidth;
        this.frameHeight = frameHeight;
        this.columns = columns;
        this.rows = rows;
        this.totalFrames = columns * rows;
        
        // Animation properties
        this.currentFrame = 0;
        this.currentRow = 0;  // Which animation (0=idle, 1=run, etc.)
        this.frameDelay = 5;  // Frames to wait before changing
        this.frameCounter = 0;
        this.loop = true;     // Should animation loop?
        
        // Load event
        this.loaded = false;
        this.image.onload = () => {
            this.loaded = true;
            console.log("Sprite sheet loaded successfully!");
        };
    }
    
    // Calculate source coordinates
    getSourceX() {
        return (this.currentFrame % this.columns) * this.frameWidth;
    }
    
    getSourceY() {
        return this.currentRow * this.frameHeight;
    }
    
    // Update animation frame
    update() {
        this.frameCounter++;
        
        // Change frame after delay
        if (this.frameCounter >= this.frameDelay) {
            this.frameCounter = 0;
            this.currentFrame++;
            
            // Loop or stop at last frame
            if (this.currentFrame >= this.columns) {
                if (this.loop) {
                    this.currentFrame = 0;
                } else {
                    this.currentFrame = this.columns - 1;
                }
            }
        }
    }
    
    // Draw current frame
    draw(ctx, x, y, width, height) {
        if (!this.loaded) return;
        
        ctx.drawImage(
            this.image,                    // Source image
            this.getSourceX(),             // Source X (crop start)
            this.getSourceY(),             // Source Y (crop start)
            this.frameWidth,               // Crop width
            this.frameHeight,              // Crop height
            x,                             // Destination X
            y,                             // Destination Y
            width || this.frameWidth,      // Draw width
            height || this.frameHeight     // Draw height
        );
    }
    
    // Change animation row (idle, run, jump)
    setAnimation(row) {
        if (row !== this.currentRow) {
            this.currentRow = row;
            this.currentFrame = 0;  // Reset to first frame
            this.frameCounter = 0;
        }
    }
}
