// =========== CHARACTER CLASS ===========
class GameCharacter {
    constructor(x, y) {
        this.x = x;
        this.y = y;
        this.width = 64;
        this.height = 64;
        this.speed = 3;
        this.isMoving = false;
        this.isJumping = false;
        this.facingRight = true;
        
        // Load sprite sheet (64x64 frames, 8 columns, 4 rows)
        this.spriteSheet = new SpriteSheet(
            'character_spritesheet.png',  // Your sprite sheet
            64, 64, 8, 4
        );
        
        // Animation rows mapping
        this.animations = {
            idle: 0,     // Row 0: Idle animation
            run: 1,      // Row 1: Running animation
            jump: 2,     // Row 2: Jumping animation
            attack: 3    // Row 3: Attack animation
        };
        
        this.currentAnimation = 'idle';
    }
    
    update(keys) {
        this.isMoving = false;
        
        // Movement logic
        if (keys['ArrowLeft']) {
            this.x -= this.speed;
            this.isMoving = true;
            this.facingRight = false;
        }
        if (keys['ArrowRight']) {
            this.x += this.speed;
            this.isMoving = true;
            this.facingRight = true;
        }
        if (keys[' '] && !this.isJumping) {
            this.isJumping = true;
            this.jumpVelocity = -15;
        }
        
        // Jumping physics
        if (this.isJumping) {
            this.y += this.jumpVelocity;
            this.jumpVelocity += 1;  // Gravity
            
            if (this.y >= groundLevel) {
                this.y = groundLevel;
                this.isJumping = false;
            }
        }
        
        // Set animation based on state
        if (this.isJumping) {
            this.currentAnimation = 'jump';
        } else if (this.isMoving) {
            this.currentAnimation = 'run';
        } else {
            this.currentAnimation = 'idle';
        }
        
        // Update sprite sheet animation
        const row = this.animations[this.currentAnimation];
        this.spriteSheet.setAnimation(row);
        this.spriteSheet.update();
    }
    
    draw(ctx) {
        // Save context to flip horizontally if facing left
        ctx.save();
        
        if (!this.facingRight) {
            ctx.translate(this.x + this.width, this.y);
            ctx.scale(-1, 1);
            this.spriteSheet.draw(ctx, 0, 0, this.width, this.height);
        } else {
            this.spriteSheet.draw(ctx, this.x, this.y, this.width, this.height);
        }
        
        ctx.restore();
    }
}

// =========== GAME SETUP ===========
const canvas = document.getElementById('gameCanvas');
const ctx = canvas.getContext('2d');
const keys = {};

const player = new GameCharacter(100, 300);
const groundLevel = 300;

// Keyboard listeners
window.addEventListener('keydown', (e) => keys[e.key] = true);
window.addEventListener('keyup', (e) => keys[e.key] = false);

// Game loop
function gameLoop() {
    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    // Update and draw
    player.update(keys);
    player.draw(ctx);
    
    // Draw ground
    ctx.fillStyle = '#8B4513';
    ctx.fillRect(0, groundLevel + 64, canvas.width, 50);
    
    requestAnimationFrame(gameLoop);
}

