class SimpleAudioPlayer {
    constructor() {
        this.sounds = {};
        this.muted = false;
        this.masterVolume = 1.0;
    }
    
    // Load a sound
    loadSound(name, filePath) {
        this.sounds[name] = new Audio(filePath);
        this.sounds[name].load();
    }
    
    // Play a sound
    play(name, volume = 1.0) {
        if (this.muted) return;
        
        const sound = this.sounds[name].cloneNode();  // Clone to play multiple times
        sound.volume = volume * this.masterVolume;
        sound.currentTime = 0;
        
        sound.play().catch(error => {
            console.log("Audio play failed:", error);
            // Some browsers require user interaction first
        });
    }
    
    // Play music with looping
    playMusic(name, volume = 0.7) {
        const music = this.sounds[name];
        music.volume = volume * this.masterVolume;
        music.loop = true;
        music.play();
    }
    
    // Stop all sounds
    stopAll() {
        for (const name in this.sounds) {
            this.sounds[name].pause();
            this.sounds[name].currentTime = 0;
        }
    }
    
    // Toggle mute
    toggleMute() {
        this.muted = !this.muted;
        for (const name in this.sounds) {
            this.sounds[name].muted = this.muted;
        }
    }
}

